237	    /* width: 100vw; */ /* Avoid 100vw due to scrollbar issues, let it be 100% or auto */
238	    background-color: hsl(var(--background)); /* Removed !important */
239	    margin: 0;
240	    padding: 0;
241	    flex-direction: column;
242	  }
243	  @media (min-width: 1024px) { .dashboard-container { flex-direction: row; } }
244	
245	  .sidebar-dark { /* Name kept, uses theme vars */
246	    background-color: hsl(var(--sidebar));
247	    border-right: 1px solid hsl(var(--sidebar-border));
248	    color: hsl(var(--sidebar-foreground));
249	    width: 100%;
250	    height: auto;
251	    overflow-y: auto;
252	    position: fixed;
253	    top: 0;
254	    left: 0;
255	    z-index: 50;
