261	    .sidebar-dark {
262	      position: static; width: 280px; height: 100vh;
263	      transform: translateX(0); flex-shrink: 0;
264	    }
265	  }
266	  .sidebar-header {
267	    padding: var(--space-6, 1.5rem);
268	    border-bottom: 1px solid hsl(var(--sidebar-border));
269	  }
