292	  }
293	  .sidebar-link.active {
294	    background-color: hsl(var(--sidebar-primary));
295	    color: hsl(var(--sidebar-primary-foreground));
296	    /* border-left: 3px solid hsl(var(--sidebar-primary)); */ /* Alternative style */
297	  }
298	
299	  .main-content {
300	    flex: 1; display: flex; flex-direction: column;
301	    min-height: 100vh; width: 100%;
302	    background-color: hsl(var(--background)); /* Removed !important */
303	    overflow-x: hidden; margin: 0; padding: 0;
304	  }
305	  @media (min-width: 1024px) { .main-content { width: calc(100% - 280px); } }
306	
307	  .dashboard-header {
308	    background-color: hsl(var(--card));
309	    border-bottom: 1px solid hsl(var(--border));
310	    padding: var(--space-4, 1rem) var(--space-6, 1.5rem);
311	    box-shadow: var(--shadow-sm, 0 1px 2px 0 rgba(0,0,0,0.05)); /* Provide fallback for shadow */
312	    position: sticky; top: 0; z-index: 40;
313	  }
314	  .dashboard-content {
315	    flex: 1; display: flex; flex-direction: column;
316	    background-color: hsl(var(--background)); /* Removed !important */
317	    /* height: calc(100vh - 80px); */ /* Avoid fixed height subtraction if possible */
318	    min-height: calc(100vh - 80px); /* Use min-height if header is fixed 80px */
319	    width: 100%; overflow: hidden; margin: 0; padding: 0;
320	  }
321	
322	  .card-dark, .modern-card, [data-radix-card], .card { /* .card might conflict if not careful with Tailwind base */
323	    background-color: hsl(var(--card)); /* Removed !important */
324	    border: 1px solid hsl(var(--border));
325	    border-radius: var(--radius, 0.375rem);
326	    padding: var(--space-6, 1.5rem);
327	    box-shadow: var(--shadow-md, 0 4px 6px -1px rgba(0,0,0,0.1), 0 2px 4px -1px rgba(0,0,0,0.06));
328	    transition: all var(--transition-normal, 200ms cubic-bezier(0.4, 0, 0.2, 1));
329	    overflow: hidden;
330	    color: hsl(var(--card-foreground)); /* Removed !important */
331	  }
332	  .card-dark:hover, .modern-card:hover, [data-radix-card]:hover, .card:hover {
333	    box-shadow: var(--shadow-lg, 0 10px 15px -3px rgba(0,0,0,0.1), 0 4px 6px -2px rgba(0,0,0,0.05));
334	    border-color: hsl(var(--border) / 0.8);
335	    transform: translateY(-2px);
336	  }
337	
338	  .metric-card-dark {
339	    background-color: hsl(var(--card));
340	    border: 1px solid hsl(var(--border));
341	    border-radius: var(--radius, 0.375rem);
342	    padding: var(--space-6, 1.5rem);
343	    box-shadow: var(--shadow-md, 0 4px 6px -1px rgba(0,0,0,0.1), 0 2px 4px -1px rgba(0,0,0,0.06));
344	    transition: all var(--transition-normal, 200ms cubic-bezier(0.4, 0, 0.2, 1));
345	    position: relative; overflow: hidden;
346	  }
347	  .metric-card-dark::before {
348	    content: ''; position: absolute; top: 0; left: 0; right: 0; height: 3px;
349	    background: linear-gradient(90deg, hsl(var(--primary)), hsl(var(--chart-2, var(--primary)) / 0.8));
350	  }
351	  .metric-title-dark {
352	    font-size: var(--text-sm, 0.875rem); color: hsl(var(--muted-foreground));
353	    font-weight: var(--font-medium, 500); margin-bottom: var(--space-2, 0.5rem);
354	    text-transform: uppercase; letter-spacing: 0.5px;
355	  }
356	  .metric-value-dark {
357	    font-size: var(--text-3xl, 1.875rem); font-weight: var(--font-bold, 700);
358	    color: hsl(var(--foreground)); margin-bottom: var(--space-2, 0.5rem);
359	    line-height: var(--leading-tight, 1.25);
360	  }
361	  .metric-change-dark {
362	    font-size: var(--text-sm, 0.875rem); font-weight: var(--font-medium, 500);
363	    display: flex; align-items: center; gap: var(--space-1, 0.25rem);
364	  }
365	  .metric-change-dark.positive { color: var(--color-success, #10B981); }
366	  .metric-change-dark.negative { color: var(--color-error, #EF4444); }
367	  .metric-change-dark.neutral { color: hsl(var(--muted-foreground) / 0.8); }
368	
369	  .nav-tabs-dark {
370	    background-color: hsl(var(--muted)); border: 1px solid hsl(var(--border));
371	    border-radius: var(--radius, 0.375rem); padding: var(--space-2, 0.5rem);
372	    margin: var(--space-4, 1rem) var(--space-6, 1.5rem) 0 var(--space-6, 1.5rem);
373	    display: flex; gap: var(--space-1, 0.25rem); flex-shrink: 0;
374	    overflow-x: auto; scrollbar-width: none; -ms-overflow-style: none; flex-wrap: nowrap;
375	  }
376	  .nav-tabs-dark::-webkit-scrollbar { display: none; }
377	  @media (max-width: 768px) { .nav-tabs-dark { margin: var(--space-2, 0.5rem) var(--space-4, 1rem) 0 var(--space-4, 1rem); } }
378	  @media (min-width: 768px) { .nav-tabs-dark { flex-wrap: wrap; overflow-x: visible; justify-content: center; } }
379	  @media (min-width: 1024px) { .nav-tabs-dark { justify-content: space-between; } }
380	
381	  .nav-tab-dark {
382	    padding: var(--space-2, 0.5rem) var(--space-3, 0.75rem);
383	    border-radius: calc(var(--radius, 0.375rem) - 2px);
384	    color: hsl(var(--muted-foreground)); font-weight: var(--font-medium, 500);
385	    font-size: var(--text-xs, 0.75rem); text-decoration: none;
386	    transition: all var(--transition-normal, 200ms cubic-bezier(0.4, 0, 0.2, 1));
387	    display: flex; align-items: center; gap: var(--space-1, 0.25rem);
388	    border: none; background: none; cursor: pointer;
389	    white-space: nowrap; flex-shrink: 0; min-width: fit-content;
390	  }
391	  @media (min-width: 640px) { .nav-tab-dark { padding: var(--space-3, 0.75rem) var(--space-4, 1rem); font-size: var(--text-sm, 0.875rem); gap: var(--space-2, 0.5rem); } }
392	  @media (min-width: 1024px) { .nav-tab-dark { flex: 1; justify-content: center; min-width: 80px; } }
393	  .nav-tab-dark:hover:not(.active) {
394	    color: hsl(var(--foreground)); background-color: hsl(var(--accent) / 0.5);
395	  }
396	  .nav-tab-dark.active {
397	    background-color: var(--color-primary-blue, hsl(var(--primary))); /* Fallback to theme primary */
398	    color: var(--color-primary-blue-fg, hsl(var(--primary-foreground))); /* Fallback to theme primary-foreground */
399	    box-shadow: var(--shadow-sm, 0 1px 2px 0 rgba(0,0,0,0.05));
400	  }
401	
402	  .badge-dark { /* Base badge style */
403	    display: inline-flex; align-items: center;
404	    padding: var(--space-1, 0.25rem) var(--space-3, 0.75rem);
405	    border-radius: var(--radius-full, 9999px);
406	    font-size: var(--text-xs, 0.75rem); font-weight: var(--font-medium, 500);
407	    text-transform: uppercase; letter-spacing: 0.5px;
408	    border: 1px solid transparent; /* Base border */
409	  }
410	  .badge-success-dark {
411	    background-color: var(--color-primary-green-bg, hsl(var(--color-success, #10B981)/0.1));
412	    color: var(--color-success, #10B981);
413	    border-color: var(--color-success, #10B981);
414	  }
415	  .badge-warning-dark {
416	    background-color: hsl(var(--color-warning, #F59E0B)/0.1);
417	    color: var(--color-warning, #F59E0B);
418	    border-color: var(--color-warning, #F59E0B);
419	  }
420	  .badge-error-dark {
421	    background-color: hsl(var(--color-error, #EF4444)/0.1);
422	    color: var(--color-error, #EF4444);
423	    border-color: var(--color-error, #EF4444);
424	  }
425	  .badge-info-dark {
426	    background-color: var(--color-primary-blue-bg, hsl(var(--color-info, #3B82F6)/0.1));
427	    color: var(--color-info, #3B82F6);
428	    border-color: var(--color-info, #3B82F6);
429	  }
430	
431	  .chart-container-dark {
432	    background-color: hsl(var(--card));
433	    border: 1px solid hsl(var(--border));
434	    border-radius: var(--radius-xl, 1rem);
435	    padding: var(--space-6, 1.5rem);
436	    box-shadow: var(--shadow-md, 0 4px 6px -1px rgba(0,0,0,0.1), 0 2px 4px -1px rgba(0,0,0,0.06));
437	    overflow: hidden;
438	  }
439	
440	  .grid-responsive {
441	    display: grid; gap: var(--space-6, 1.5rem); grid-template-columns: 1fr;
442	  }
443	  @media (min-width: 640px) { .grid-responsive { grid-template-columns: repeat(2, 1fr); } }
444	  @media (min-width: 1024px) { .grid-responsive { grid-template-columns: repeat(3, 1fr); } }
445	  @media (min-width: 1280px) { .grid-responsive { grid-template-columns: repeat(4, 1fr); } }
446	
447	  .mobile-overlay {
448	    position: fixed; inset: 0;
449	    background-color: hsl(var(--background) / 0.8); /* Overlay with background color */
450	    z-index: 40; display: block;
451	  }
452	  @media (min-width: 1024px) { .mobile-overlay { display: none; } }
453	
454	  .loading-spinner {
455	    width: 24px; height: 24px;
456	    border: 2px solid hsl(var(--border));
457	    border-top: 2px solid var(--color-primary-blue, hsl(var(--primary)));
458	    border-radius: 50%;
459	    animation: spin 1s linear infinite;
460	  }
461	
462	  .mobile-menu-btn {
463	    display: flex; align-items: center; justify-content: center;
464	    width: 40px; height: 40px; border: none; background: none;
465	    color: hsl(var(--muted-foreground)); cursor: pointer;
466	    border-radius: var(--radius-lg, 0.75rem);
467	    transition: all var(--transition-normal, 200ms cubic-bezier(0.4, 0, 0.2, 1));
468	  }
469	  .mobile-menu-btn:hover {
470	    background-color: hsl(var(--accent) / 0.5);
471	    color: hsl(var(--accent-foreground));
472	  }
473	  @media (min-width: 1024px) { .mobile-menu-btn { display: none; } }
474	
475	  .text-primary { color: hsl(var(--primary-foreground)); }
476	  .text-secondary { color: hsl(var(--secondary-foreground)); }
477	  .text-tertiary { color: hsl(var(--muted-foreground)); }
478	
479	  .bg-primary { background-color: hsl(var(--primary)); }
480	  .bg-secondary { background-color: hsl(var(--secondary)); }
481	  .bg-elevated { background-color: hsl(var(--popover)); }
482	
483	  .border-primary { border-color: hsl(var(--border)); }
484	  .border-secondary { border-color: hsl(var(--input)); }
485	
486	  .dashboard-content > div:last-child {
487	    flex: 1; display: flex; flex-direction: column; min-height: 0;
488	  }
489	}
490	
491	/* Keyframes (merged) */
492	@keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
493	@keyframes slideIn { from { transform: translateX(-100%); } to { transform: translateX(0); } }
494	@keyframes pulse { /* For animate-pulse-slow if not provided by Tailwind */
495	  0%, 100% { opacity: 1; }
496	  50% { opacity: .5; }
497	}
498	@keyframes slideUp { from { opacity: 0; transform: translateY(20px); } to { opacity: 1; transform: translateY(0); } }
499	@keyframes scaleIn { from { opacity: 0; transform: scale(0.95); } to { opacity: 1; transform: scale(1); } }
500	@keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); } }
501	
502	@layer utilities {
503	  /* Original utilities from globals.css */
504	  .text-gradient {
505	    @apply bg-gradient-to-r from-emerald-600 via-violet-600 to-amber-600 bg-clip-text text-transparent;
506	  }
507	  .glass-effect {
508	    @apply bg-background/80 backdrop-blur-sm border border-border/50; /* Uses new theme vars */
509	  }
510	  .trading-grid {
511	    display: grid;
512	    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
513	    gap: 1rem;
514	  }
515	  .dashboard-grid {
516	    display: grid;
517	    grid-template-columns: 250px 1fr;
518	    grid-template-rows: auto 1fr;
519	    min-height: 100vh;
520	  }
521	  .sidebar-grid {
522	    grid-column: 1;
523	    grid-row: 1 / -1;
524	  }
525	  .main-content { /* This is from original utilities. It might conflict or co-exist with component .main-content */
526	    grid-column: 2;
527	    grid-row: 1 / -1;
528	  }
529	  @media (max-width: 768px) {
530	    .dashboard-grid {
531	      grid-template-columns: 1fr;
532	      grid-template-rows: auto auto 1fr;
533	    }
534	    .sidebar-grid {
535	      grid-column: 1;
536	      grid-row: 2;
537	    }
538	    .main-content { /* from original utilities */
539	      grid-column: 1;
540	      grid-row: 3;
541	    }
542	  }
543	  /* High contrast and reduced motion from globals-dark.css, moved to utilities */
544	  @media (prefers-contrast: high) {
545	    :root, .dark { /* Apply to all themes, adjusted for simplicity */
546	      --foreground: oklch(0.95 0 0);
547	      --card-foreground: oklch(0.95 0 0);
548	      --muted-foreground: oklch(0.85 0 0);
549	      --border: oklch(0.7 0 0);
550	    }
551	    .dark { /* Specific adjustments for dark if needed beyond direct variable inversion */
552	      --foreground: oklch(0.10 0 0);
553	      --card-foreground: oklch(0.10 0 0);
554	      --muted-foreground: oklch(0.20 0 0);
555	      --border: oklch(0.30 0 0);
556	    }
557	  }
558	  @media (prefers-reduced-motion: reduce) {
559	    * {
560	      animation-duration: 0.01ms !important;
561	      animation-iteration-count: 1 !important;
562	      transition-duration: 0.01ms !important;
563	      scroll-behavior: auto !important;
564	    }
565	  }
566	}
