 236	function getStatusColor(status: string) {
 237	  switch (status) {
 238	    case 'active': return 'text-status-online';
 239	    case 'locked': return 'text-status-warning';
 240	    case 'pending': return 'text-blue-500';
 241	    case 'completed': return 'text-status-online';
 242	    case 'failed': return 'text-status-error';
 243	    default: return 'text-muted-foreground';
 244	  }
 245	}
 246	
 247	function getRiskColor(level: string) {
 248	  switch (level) {
 249	    case 'Minimal': return 'text-green-600';
 250	    case 'Low': return 'text-status-online';
 251	    case 'Medium': return 'text-status-warning';
 252	    case 'High': return 'text-status-error';
 253	    default: return 'text-muted-foreground';
 254	  }
 255	}
 256	
 257	function getTransactionIcon(type: string) {
 258	  switch (type) {
 259	    case 'transfer': return <ArrowLeftRight className="h-4 w-4" />;
 260	    case 'deposit': return <ArrowDownLeft className="h-4 w-4 text-status-online" />;
 261	    case 'withdrawal': return <ArrowUpRight className="h-4 w-4 text-status-error" />;
 262	    case 'yield': return <TrendingUp className="h-4 w-4 text-trading-profit" />;
 263	    default: return <DollarSign className="h-4 w-4" />;
 264	  }
 265	}
