const TerminalHistory = {
  save: (command: string) => {
    const history = JSON.parse(localStorage.getItem('terminal-history') || '[]');
    history.push({ command, timestamp: Date.now() });
    localStorage.setItem('terminal-history', JSON.stringify(history.slice(-100)));
  },

  search: (query: string) => {
    const history = JSON.parse(localStorage.getItem('terminal-history') || '[]');
    return history.filter(item => item.command.includes(query));
  },

  createMacro: (name: string, commands: string[]) => {
    const macros = JSON.parse(localStorage.getItem('terminal-macros') || '{}');
    macros[name] = commands;
    localStorage.setItem('terminal-macros', JSON.stringify(macros));
  },

  executeMacro: async (name: string) => {
    const macros = JSON.parse(localStorage.getItem('terminal-macros') || '{}');
    const commands = macros[name];
    if (!commands) return { type: 'error', content: `Macro ${name} not found` };

    for (const command of commands) {
      await executeCommand(command);
    }
  }
};
