'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { Slider } from '@/components/ui/slider';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Separator } from '@/components/ui/separator';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { PremiumThemeProvider } from '../theme/PremiumThemeProvider';
import { TradingWebSocketClient } from '@/lib/websocket/trading-client';
import { usePerformanceMonitor } from '@/hooks/usePerformanceMonitor';
import { 
  TrendingUp, 
  TrendingDown,
  BarChart3,
  LineChart as LineChartIcon,
  Target,
  Zap,
  AlertTriangle,
  CheckCircle2,
  Play,
  Pause,
  Settings,
  Plus,
  Eye,
  Edit3,
  Copy,
  Download
} from 'lucide-react';
import {
  LineChart, Line,
  BarChart, Bar,
  AreaChart, Area,
  ScatterChart, Scatter,
  RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, Radar,
  ResponsiveContainer,
  XAxis, YAxis,
  CartesianGrid, Tooltip,
  Legend,
  ReferenceLine
} from 'recharts';

interface Scenario {
  id: string;
  name: string;
  description: string;
  type: 'market_condition' | 'economic_event' | 'geopolitical' | 'regulatory' | 'company_specific' | 'custom';
  probability: number;
  timeHorizon: number;
  parameters: ScenarioParameter[];
  impacts: ScenarioImpact[];
  status: 'draft' | 'active' | 'completed' | 'archived';
  createdAt: Date;
  lastRun?: Date;
  results?: ScenarioResults;
}

interface ScenarioParameter {
  factor: string;
  currentValue: number;
  scenarioValue: number;
  unit: string;
  confidenceLevel: number;
}

interface ScenarioImpact {
  asset: string;
  category: 'equity' | 'bond' | 'commodity' | 'currency' | 'alternative';
  expectedReturn: number;
  volatility: number;
  correlation: number;
}

interface ScenarioResults {
  portfolioImpact: number;
  assetImpacts: AssetImpact[];
  riskMetrics: ScenarioRiskMetrics;
  timeSeriesData: TimeSeriesPoint[];
  confidenceIntervals: ConfidenceInterval[];
}

interface AssetImpact {
  asset: string;
  currentPrice: number;
  scenarioPrice: number;
  priceChange: number;
  percentChange: number;
  volumeImpact: number;
}

interface ScenarioRiskMetrics {
  var95: number;
  var99: number;
  expectedShortfall: number;
  maxDrawdown: number;
  sharpeRatio: number;
  correlationBreakdown: number;
}

interface TimeSeriesPoint {
  time: number;
  portfolioValue: number;
  cumReturn: number;
  drawdown: number;
  volatility: number;
}

interface ConfidenceInterval {
  percentile: number;
  value: number;
}

interface ScenarioTemplate {
  id: string;
  name: string;
  description: string;
  category: string;
  parameters: Omit<ScenarioParameter, 'currentValue'>[];
  impacts: Omit<ScenarioImpact, 'expectedReturn' | 'volatility'>[];
}

const mockScenarios: Scenario[] = [
  {
    id: 'scenario-1',
    name: 'Federal Reserve Rate Hike',
    description: 'Aggressive interest rate increases to combat inflation',
    type: 'economic_event',
    probability: 65,
    timeHorizon: 180,
    parameters: [
      {
        factor: 'Fed Funds Rate',
        currentValue: 5.25,
        scenarioValue: 7.0,
        unit: '%',
        confidenceLevel: 80
      },
      {
        factor: '10Y Treasury Yield',
        currentValue: 4.5,
        scenarioValue: 6.0,
        unit: '%',
        confidenceLevel: 75
      }
    ],
    impacts: [
      {
        asset: 'SPY',
        category: 'equity',
        expectedReturn: -15,
        volatility: 25,
        correlation: 0.8
      },
      {
        asset: 'TLT',
        category: 'bond',
        expectedReturn: -8,
        volatility: 12,
        correlation: -0.3
      }
    ],
    status: 'active',
    createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 5),
    lastRun: new Date(Date.now() - 1000 * 60 * 60 * 2),
    results: {
      portfolioImpact: -12.8,
      assetImpacts: [
        {
          asset: 'AAPL',
          currentPrice: 180,
          scenarioPrice: 153,
          priceChange: -27,
          percentChange: -15,
          volumeImpact: 25
        },
        {
          asset: 'GOOGL',
          currentPrice: 140,
          scenarioPrice: 126,
          priceChange: -14,
          percentChange: -10,
          volumeImpact: 18
        }
      ],
      riskMetrics: {
        var95: 1250000,
        var99: 1850000,
        expectedShortfall: 2100000,
        maxDrawdown: -18.5,
        sharpeRatio: -0.45,
        correlationBreakdown: 85
      },
      timeSeriesData: [],
      confidenceIntervals: [
        { percentile: 5, value: -25.2 },
        { percentile: 25, value: -18.1 },
        { percentile: 50, value: -12.8 },
        { percentile: 75, value: -8.4 },
        { percentile: 95, value: -2.1 }
      ]
    }
  },
  {
    id: 'scenario-2',
    name: 'Geopolitical Tension Escalation',
    description: 'Major geopolitical crisis affecting global markets',
    type: 'geopolitical',
    probability: 35,
    timeHorizon: 90,
    parameters: [
      {
        factor: 'VIX Level',
        currentValue: 18,
        scenarioValue: 45,
        unit: 'index',
        confidenceLevel: 70
      },
      {
        factor: 'Oil Price',
        currentValue: 85,
        scenarioValue: 120,
        unit: '$/barrel',
        confidenceLevel: 65
      }
    ],
    impacts: [
      {
        asset: 'SPY',
        category: 'equity',
        expectedReturn: -25,
        volatility: 35,
        correlation: 0.9
      },
      {
        asset: 'GLD',
        category: 'commodity',
        expectedReturn: 15,
        volatility: 20,
        correlation: -0.4
      }
    ],
    status: 'draft',
    createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 2)
  },
  {
    id: 'scenario-3',
    name: 'Tech Sector Correction',
    description: 'Major correction in technology stocks due to valuation concerns',
    type: 'market_condition',
    probability: 45,
    timeHorizon: 120,
    parameters: [
      {
        factor: 'NASDAQ P/E Ratio',
        currentValue: 28,
        scenarioValue: 18,
        unit: 'ratio',
        confidenceLevel: 85
      },
      {
        factor: 'Tech Sector Weight',
        currentValue: 30,
        scenarioValue: 22,
        unit: '%',
        confidenceLevel: 75
      }
    ],
    impacts: [
      {
        asset: 'QQQ',
        category: 'equity',
        expectedReturn: -35,
        volatility: 40,
        correlation: 0.95
      },
      {
        asset: 'AAPL',
        category: 'equity',
        expectedReturn: -30,
        volatility: 35,
        correlation: 0.8
      }
    ],
    status: 'completed',
    createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 10),
    lastRun: new Date(Date.now() - 1000 * 60 * 60 * 24 * 7)
  }
];

const scenarioTemplates: ScenarioTemplate[] = [
  {
    id: 'template-1',
    name: 'Market Crash',
    description: 'Severe equity market decline scenario',
    category: 'market_condition',
    parameters: [
      {
        factor: 'Market Decline',
        scenarioValue: -30,
        unit: '%',
        confidenceLevel: 80
      }
    ],
    impacts: [
      {
        asset: 'SPY',
        category: 'equity',
        correlation: 0.9
      }
    ]
  },
  {
    id: 'template-2',
    name: 'Inflation Surge',
    description: 'Unexpected inflation acceleration',
    category: 'economic_event',
    parameters: [
      {
        factor: 'CPI Rate',
        scenarioValue: 8.0,
        unit: '%',
        confidenceLevel: 75
      }
    ],
    impacts: [
      {
        asset: 'TLT',
        category: 'bond',
        correlation: -0.7
      }
    ]
  }
];

const generateScenarioTimeSeries = () => {
  const data = [];
  let portfolioValue = 10000000;
  let cumReturn = 0;

  for (let i = 0; i <= 180; i++) {
    const dailyReturn = (Math.random() - 0.6) * 0.02; // Negative bias for stress scenario
    portfolioValue *= (1 + dailyReturn);
    cumReturn = (portfolioValue / 10000000 - 1) * 100;

    data.push({
      time: i,
      portfolioValue,
      cumReturn,
      drawdown: Math.min(0, cumReturn),
      volatility: 15 + Math.random() * 10
    });
  }
  return data;
};

export function ScenarioAnalysis() {
  const [ws, setWs] = useState<TradingWebSocketClient | null>(null);
  const [scenarios, setScenarios] = useState<Scenario[]>(mockScenarios);
  const [selectedScenario, setSelectedScenario] = useState<Scenario | null>(null);
  const [isRunning, setIsRunning] = useState(false);
  const [progress, setProgress] = useState(0);
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [selectedTemplate, setSelectedTemplate] = useState<string>('');
  const [timeSeriesData] = useState(generateScenarioTimeSeries());

  const performanceMetrics = usePerformanceMonitor('ScenarioAnalysis');

  useEffect(() => {
    const wsClient = TradingWebSocketClient.getInstance('ws://localhost:8000/ws');
    setWs(wsClient);

    // Subscribe to scenario updates
    wsClient.subscribe('scenario.update', (data: any) => {
      if (data.scenario) {
        setScenarios(prev => prev.map(s => s.id === data.scenario.id ? data.scenario : s));
      }
      if (data.progress) {
        setProgress(data.progress);
      }
    });

    return () => {
      wsClient.unsubscribe('scenario.update');
    };
  }, []);

  const runScenario = async (scenarioId: string) => {
    setIsRunning(true);
    setProgress(0);

    // Simulate scenario execution
    const interval = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setIsRunning(false);
          return 100;
        }
        return prev + Math.random() * 8;
      });
    }, 400);
  };

  const getStatusBadge = (status: string) => {
    const colors = {
      draft: 'secondary',
      active: 'default',
      completed: 'secondary',
      archived: 'outline'
    };
    return <Badge variant={colors[status as keyof typeof colors] as any}>{status}</Badge>;
  };

  const getProbabilityColor = (probability: number) => {
    if (probability >= 70) return 'text-red-500';
    if (probability >= 40) return 'text-yellow-500';
    return 'text-green-500';
  };

  const scenarioStats = {
    total: scenarios.length,
    active: scenarios.filter(s => s.status === 'active').length,
    highProbability: scenarios.filter(s => s.probability >= 60).length,
    completed: scenarios.filter(s => s.status === 'completed').length
  };

  return (
    <PremiumThemeProvider>
      <div className="w-full space-y-4">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold flex items-center gap-2">
              <BarChart3 className="h-6 w-6 text-blue-500" />
              Scenario Analysis
            </h2>
            <p className="text-sm text-muted-foreground">
              Model and analyze potential market scenarios and their portfolio impacts
            </p>
          </div>
          <div className="flex items-center gap-2">
            <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
              <DialogTrigger asChild>
                <Button>
                  <Plus className="h-4 w-4 mr-2" />
                  New Scenario
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-3xl">
                <DialogHeader>
                  <DialogTitle>Create Scenario</DialogTitle>
                  <DialogDescription>
                    Define a new market scenario for analysis
                  </DialogDescription>
                </DialogHeader>
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label>Scenario Name</Label>
                      <Input placeholder="Enter scenario name" />
                    </div>
                    <div>
                      <Label>Template</Label>
                      <Select value={selectedTemplate} onValueChange={setSelectedTemplate}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select template" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="none">Start from scratch</SelectItem>
                          {scenarioTemplates.map(template => (
                            <SelectItem key={template.id} value={template.id}>
                              {template.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                  </div>
                  <div>
                    <Label>Description</Label>
                    <Textarea placeholder="Describe the scenario and its key drivers" />
                  </div>
                  <div className="grid grid-cols-3 gap-4">
                    <div>
                      <Label>Type</Label>
                      <Select>
                        <SelectTrigger>
                          <SelectValue placeholder="Select type" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="market_condition">Market Condition</SelectItem>
                          <SelectItem value="economic_event">Economic Event</SelectItem>
                          <SelectItem value="geopolitical">Geopolitical</SelectItem>
                          <SelectItem value="regulatory">Regulatory</SelectItem>
                          <SelectItem value="company_specific">Company Specific</SelectItem>
                          <SelectItem value="custom">Custom</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div>
                      <Label>Probability (%)</Label>
                      <Input type="number" min="1" max="100" placeholder="50" />
                    </div>
                    <div>
                      <Label>Time Horizon (days)</Label>
                      <Input type="number" min="1" max="365" placeholder="90" />
                    </div>
                  </div>
                </div>
                <DialogFooter>
                  <Button variant="outline" onClick={() => setIsCreateDialogOpen(false)}>
                    Cancel
                  </Button>
                  <Button onClick={() => setIsCreateDialogOpen(false)}>
                    Create Scenario
                  </Button>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          </div>
        </div>

        {/* Stats Overview */}
        <div className="grid grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Total Scenarios</p>
                  <p className="text-2xl font-bold">{scenarioStats.total}</p>
                </div>
                <BarChart3 className="h-8 w-8 text-blue-500 opacity-20" />
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Active</p>
                  <p className="text-2xl font-bold text-green-500">{scenarioStats.active}</p>
                </div>
                <CheckCircle2 className="h-8 w-8 text-green-500 opacity-20" />
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">High Probability</p>
                  <p className="text-2xl font-bold text-red-500">{scenarioStats.highProbability}</p>
                </div>
                <AlertTriangle className="h-8 w-8 text-red-500 opacity-20" />
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Completed</p>
                  <p className="text-2xl font-bold">{scenarioStats.completed}</p>
                </div>
                <Target className="h-8 w-8 text-gray-500 opacity-20" />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Running Scenario Progress */}
        {isRunning && (
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between mb-2">
                <h4 className="font-medium">Running Scenario Analysis</h4>
                <span className="text-sm text-muted-foreground">{Math.round(progress)}%</span>
              </div>
              <Progress value={progress} className="mb-2" />
              <p className="text-sm text-muted-foreground">
                Analyzing {selectedScenario?.parameters.length} parameters across {selectedScenario?.impacts.length} assets...
              </p>
            </CardContent>
          </Card>
        )}

        {/* Main Content */}
        <div className="grid grid-cols-3 gap-4">
          <div className="col-span-2">
            <Tabs defaultValue="scenarios" className="w-full">
              <TabsList className="grid w-full grid-cols-3">
                <TabsTrigger value="scenarios">Scenarios</TabsTrigger>
                <TabsTrigger value="results">Results</TabsTrigger>
                <TabsTrigger value="comparison">Comparison</TabsTrigger>
              </TabsList>

              <TabsContent value="scenarios" className="space-y-4">
                <ScrollArea className="h-[600px]">
                  <div className="space-y-4">
                    {scenarios.map((scenario) => (
                      <Card 
                        key={scenario.id}
                        className={`cursor-pointer transition-colors ${
                          selectedScenario?.id === scenario.id ? 'border-primary' : ''
                        }`}
                        onClick={() => setSelectedScenario(scenario)}
                      >
                        <CardContent className="p-4">
                          <div className="flex items-start justify-between mb-3">
                            <div>
                              <h4 className="font-medium">{scenario.name}</h4>
                              <p className="text-sm text-muted-foreground mt-1">
                                {scenario.description}
                              </p>
                              <div className="flex items-center gap-2 mt-2">
                                <Badge variant="outline">{scenario.type.replace('_', ' ')}</Badge>
                                {getStatusBadge(scenario.status)}
                                <Badge 
                                  variant="outline"
                                  className={getProbabilityColor(scenario.probability)}
                                >
                                  {scenario.probability}% probability
                                </Badge>
                              </div>
                            </div>
                            <div className="flex items-center gap-2">
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  // Copy scenario
                                }}
                              >
                                <Copy className="h-4 w-4" />
                              </Button>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  // Edit scenario
                                }}
                              >
                                <Edit3 className="h-4 w-4" />
                              </Button>
                              <Button
                                variant="default"
                                size="sm"
                                disabled={isRunning}
                                onClick={(e) => {
                                  e.stopPropagation();
                                  runScenario(scenario.id);
                                }}
                              >
                                <Play className="h-4 w-4" />
                              </Button>
                            </div>
                          </div>

                          <div className="grid grid-cols-2 gap-4">
                            <div>
                              <p className="text-sm text-muted-foreground">Time Horizon</p>
                              <p className="font-medium">{scenario.timeHorizon} days</p>
                            </div>
                            <div>
                              <p className="text-sm text-muted-foreground">Parameters</p>
                              <p className="font-medium">{scenario.parameters.length}</p>
                            </div>
                          </div>

                          {scenario.results && (
                            <div className="mt-3 pt-3 border-t">
                              <div className="grid grid-cols-3 gap-4 text-sm">
                                <div>
                                  <p className="text-muted-foreground">Portfolio Impact</p>
                                  <p className={`font-medium ${
                                    scenario.results.portfolioImpact >= 0 ? 'text-green-500' : 'text-red-500'
                                  }`}>
                                    {scenario.results.portfolioImpact}%
                                  </p>
                                </div>
                                <div>
                                  <p className="text-muted-foreground">Max Drawdown</p>
                                  <p className="font-medium text-red-500">
                                    {scenario.results.riskMetrics.maxDrawdown}%
                                  </p>
                                </div>
                                <div>
                                  <p className="text-muted-foreground">VaR 95%</p>
                                  <p className="font-medium">
                                    ${scenario.results.riskMetrics.var95.toLocaleString()}
                                  </p>
                                </div>
                              </div>
                            </div>
                          )}

                          <div className="mt-3 pt-3 border-t">
                            <p className="text-xs text-muted-foreground mb-1">Key Parameters:</p>
                            <div className="space-y-1">
                              {scenario.parameters.slice(0, 2).map((param, index) => (
                                <div key={index} className="text-xs text-muted-foreground">
                                  {param.factor}: {param.currentValue}{param.unit}  {param.scenarioValue}{param.unit}
                                </div>
                              ))}
                            </div>
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </ScrollArea>
              </TabsContent>

              <TabsContent value="results" className="space-y-4">
                {selectedScenario?.results ? (
                  <div className="space-y-4">
                    <Card>
                      <CardHeader>
                        <CardTitle>Portfolio Impact Analysis</CardTitle>
                        <CardDescription>Scenario impact on portfolio performance</CardDescription>
                      </CardHeader>
                      <CardContent>
                        <div className="grid grid-cols-2 gap-6">
                          <div className="space-y-4">
                            <div>
                              <p className="text-sm text-muted-foreground">Total Impact</p>
                              <p className={`text-3xl font-bold ${
                                selectedScenario.results.portfolioImpact >= 0 ? 'text-green-500' : 'text-red-500'
                              }`}>
                                {selectedScenario.results.portfolioImpact}%
                              </p>
                            </div>
                            <div>
                              <p className="text-sm text-muted-foreground">Sharpe Ratio</p>
                              <p className="text-2xl font-bold">
                                {selectedScenario.results.riskMetrics.sharpeRatio}
                              </p>
                            </div>
                            <div>
                              <p className="text-sm text-muted-foreground">Expected Shortfall</p>
                              <p className="text-2xl font-bold text-red-500">
                                ${selectedScenario.results.riskMetrics.expectedShortfall.toLocaleString()}
                              </p>
                            </div>
                          </div>
                          <div className="h-64">
                            <ResponsiveContainer width="100%" height="100%">
                              <AreaChart data={timeSeriesData.slice(0, 50)}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="time" />
                                <YAxis />
                                <Tooltip />
                                <Area 
                                  type="monotone" 
                                  dataKey="cumReturn" 
                                  stroke="#ef4444" 
                                  fill="#ef4444" 
                                  fillOpacity={0.3}
                                />
                                <ReferenceLine y={0} stroke="#666" strokeDasharray="2 2" />
                              </AreaChart>
                            </ResponsiveContainer>
                          </div>
                        </div>
                      </CardContent>
                    </Card>

                    <div className="grid grid-cols-2 gap-4">
                      <Card>
                        <CardHeader>
                          <CardTitle>Asset Impact Distribution</CardTitle>
                        </CardHeader>
                        <CardContent>
                          <ScrollArea className="h-48">
                            <div className="space-y-3">
                              {selectedScenario.results.assetImpacts.map((asset) => (
                                <div key={asset.asset} className="flex items-center justify-between">
                                  <div>
                                    <span className="font-medium">{asset.asset}</span>
                                    <div className="text-sm text-muted-foreground">
                                      ${asset.currentPrice}  ${asset.scenarioPrice}
                                    </div>
                                  </div>
                                  <div className="text-right">
                                    <div className={`font-medium ${
                                      asset.percentChange >= 0 ? 'text-green-500' : 'text-red-500'
                                    }`}>
                                      {asset.percentChange}%
                                    </div>
                                    <div className="text-sm text-muted-foreground">
                                      ${asset.priceChange}
                                    </div>
                                  </div>
                                </div>
                              ))}
                            </div>
                          </ScrollArea>
                        </CardContent>
                      </Card>

                      <Card>
                        <CardHeader>
                          <CardTitle>Risk Distribution</CardTitle>
                        </CardHeader>
                        <CardContent>
                          <div className="h-48">
                            <ResponsiveContainer width="100%" height="100%">
