302	    const day = new Date();
303	    day.setDate(day.getDate() - i);
304	    const point: any = {
305	      date: day.toLocaleDateString(),
306	      'Base Case': 100 + Math.random() * 5
307	    };
308	    
309	    point['Rate Hike'] = point['Base Case'] * (0.92 + Math.random() * 0.05);
310	    point['Tech Correction'] = point['Base Case'] * (0.85 + Math.random() * 0.08);
311	    point['Crisis'] = point['Base Case'] * (0.75 + Math.random() * 0.10);
312	    
313	    data.push(point);
314	  }
315	  return data;
316	};
