'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { ScrollArea } from '@/components/ui/scroll-area';
import { PremiumThemeProvider } from '../theme/PremiumThemeProvider';
import { TradingWebSocketClient } from '@/lib/websocket/trading-client';
import { usePerformanceMonitor } from '@/hooks/usePerformanceMonitor';
import { 
  Building2, 
  Users, 
  Database,
  Settings,
  BarChart3,
  Cpu,
  HardDrive,
  Network,
  Shield,
  Zap
} from 'lucide-react';
import {
  BarChart, Bar,
  LineChart, Line,
  PieChart, Pie, Cell,
  ResponsiveContainer,
  XAxis, YAxis,
  CartesianGrid, Tooltip,
  Legend
} from 'recharts';

interface Tenant {
  id: string;
  name: string;
  domain: string;
  plan: 'basic' | 'professional' | 'enterprise';
  status: 'active' | 'suspended' | 'trial';
  userCount: number;
  dataUsage: number;
  cpuUsage: number;
  memoryUsage: number;
  storageUsage: number;
  createdAt: Date;
  lastActivity: Date;
  limits: TenantLimits;
  features: TenantFeatures;
}

interface TenantLimits {
  maxUsers: number;
  maxPortfolios: number;
  maxStrategies: number;
  maxApiCalls: number;
  maxStorageGB: number;
  maxCpuHours: number;
}

interface TenantFeatures {
  realTimeData: boolean;
  advancedAnalytics: boolean;
  multiAssetTrading: boolean;
  riskManagement: boolean;
  compliance: boolean;
  apiAccess: boolean;
  customBranding: boolean;
  ssoIntegration: boolean;
}

interface ResourceUsage {
  cpu: number;
  memory: number;
  storage: number;
  network: number;
  database: number;
}

interface TenantMetrics {
  totalTenants: number;
  activeTenants: number;
  trialTenants: number;
  totalUsers: number;
  totalRevenue: number;
  resourceUtilization: ResourceUsage;
  growthMetrics: GrowthMetric[];
  planDistribution: PlanDistribution[];
}

interface GrowthMetric {
  date: string;
  newTenants: number;
  activeUsers: number;
  revenue: number;
}

interface PlanDistribution {
  plan: string;
  count: number;
  revenue: number;
  percentage: number;
}

// Mock data for solo operator with multiple trading environments/contexts
const mockTenants: Tenant[] = [
  {
    id: 'main',
    name: 'Main Trading Environment',
    domain: 'main.trading.local',
    plan: 'enterprise',
