    userCount: 1,
    dataUsage: 15,
    cpuUsage: 8,
    memoryUsage: 18,
    storageUsage: 5,
    createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 30),
    lastActivity: new Date(Date.now() - 1000 * 60 * 60 * 2),
    limits: {
      maxUsers: 1,
      maxPortfolios: 5,
      maxStrategies: 10,
