  Legend
} from 'recharts';

interface AuditLog {
  id: string;
  timestamp: Date;
  level: 'info' | 'warning' | 'error' | 'critical' | 'debug';
  category: 'trading' | 'system' | 'security' | 'data' | 'api' | 'user';
  action: string;
  resource: string;
  userId?: string;
  userEmail?: string;
  ipAddress: string;
  userAgent: string;
  sessionId?: string;
  details: string;
  metadata: Record<string, any>;
  source: string;
  correlationId?: string;
}

interface LogFilter {
  dateRange: { from: Date; to: Date };
  levels: string[];
  categories: string[];
  users: string[];
  searchQuery: string;
}

interface LogMetrics {
  totalLogs: number;
  logsByLevel: Record<string, number>;
  logsByCategory: Record<string, number>;
  recentActivity: ActivityMetric[];
  topUsers: UserActivity[];
  systemHealth: HealthMetric[];
}

interface ActivityMetric {
  hour: string;
  info: number;
  warning: number;
  error: number;
  critical: number;
}

interface UserActivity {
  userId: string;
  userEmail: string;
  logCount: number;
  lastActivity: Date;
}

interface HealthMetric {
  timestamp: Date;
  errorRate: number;
  responseTime: number;
  throughput: number;
}

const mockLogs: AuditLog[] = [
  {
    id: 'log-1',
    timestamp: new Date(Date.now() - 1000 * 60 * 5),
    level: 'info',
    category: 'trading',
    action: 'order.placed',
    resource: 'Order-12345',
    userId: 'solo-operator',
    userEmail: 'trader@local.dev',
    ipAddress: '127.0.0.1',
    userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64)',
    sessionId: 'sess-abc123',
    details: 'Successfully placed limit order for 100 shares of AAPL at $150.00',
    metadata: {
      symbol: 'AAPL',
      quantity: 100,
