const generateMetrics = (): LogMetrics => {
  const activityData: ActivityMetric[] = [];
  for (let i = 23; i >= 0; i--) {
    const hour = new Date();
    hour.setHours(hour.getHours() - i);
    activityData.push({
      hour: hour.getHours().toString().padStart(2, '0') + ':00',
      info: Math.floor(Math.random() * 50) + 20,
      warning: Math.floor(Math.random() * 15) + 5,
      error: Math.floor(Math.random() * 8) + 2,
      critical: Math.floor(Math.random() * 3)
    });
  }

  const healthData: HealthMetric[] = [];
  for (let i = 60; i >= 0; i--) {
    const timestamp = new Date();
    timestamp.setMinutes(timestamp.getMinutes() - i);
    healthData.push({
      timestamp,
      errorRate: Math.random() * 5,
      responseTime: Math.random() * 200 + 50,
      throughput: Math.random() * 1000 + 500
    });
  }

  return {
    totalLogs: 15847,
    logsByLevel: {
      info: 12456,
      warning: 2145,
      error: 987,
      critical: 89,
      debug: 170
    },
    logsByCategory: {
      trading: 8456,
      system: 3421,
      security: 1876,
      data: 1254,
      api: 654,
      user: 186
    },
    recentActivity: activityData,
    topUsers: [
      {
        userId: 'solo-operator',
        userEmail: 'trader@local.dev',
        logCount: 12456,
        lastActivity: new Date(Date.now() - 1000 * 60 * 5)
      },
      {
        userId: 'system',
        userEmail: 'system@local.dev',
        logCount: 3421,
        lastActivity: new Date(Date.now() - 1000 * 60 * 2)
      }
    ],
    systemHealth: healthData
  };
};

const columns: ColumnDef<AuditLog>[] = [
  {
    accessorKey: 'timestamp',
    header: 'Time',
