interface AuditLog {
  id: string;
  timestamp: Date;
  level: 'info' | 'warning' | 'error' | 'critical' | 'debug';
  category: 'trading' | 'system' | 'security' | 'data' | 'api' | 'user';
  action: string;
  resource: string;
  userId?: string;
  userEmail?: string;
  ipAddress: string;
  userAgent: string;
  sessionId?: string;
  details: string;
  metadata: Record<string, any>;
  source: string;
  correlationId?: string;
}

interface LogFilter {
  dateRange: { from: Date; to: Date };
  levels: string[];
  categories: string[];
  users: string[];
  searchQuery: string;
}

interface LogMetrics {
  totalLogs: number;
  logsByLevel: Record<string, number>;
  logsByCategory: Record<string, number>;
  recentActivity: ActivityMetric[];
  topUsers: UserActivity[];
  systemHealth: HealthMetric[];
}

interface ActivityMetric {
  hour: string;
  info: number;
  warning: number;
  error: number;
  critical: number;
}

interface UserActivity {
  userId: string;
  userEmail: string;
  logCount: number;
  lastActivity: Date;
}

interface HealthMetric {
  timestamp: Date;
  errorRate: number;
  responseTime: number;
  throughput: number;
}

