} from 'recharts';

interface SecurityPolicy {
  id: string;
  name: string;
  category: 'authentication' | 'authorization' | 'encryption' | 'network' | 'data' | 'compliance';
  status: 'enabled' | 'disabled' | 'warning';
  enforced: boolean;
  lastUpdated: Date;
  compliance: string[];
  description: string;
  riskLevel: 'low' | 'medium' | 'high' | 'critical';
}

interface SecurityThreat {
  id: string;
  type: 'malware' | 'phishing' | 'bruteforce' | 'ddos' | 'insider' | 'vulnerability';
  severity: 'low' | 'medium' | 'high' | 'critical';
  status: 'detected' | 'investigating' | 'mitigated' | 'resolved';
  source: string;
  target: string;
  timestamp: Date;
  description: string;
  impact: string;
  mitigation: string[];
}

interface SecurityMetrics {
  overallScore: number;
  vulnerabilities: {
    critical: number;
    high: number;
    medium: number;
    low: number;
  };
  threatLevel: 'low' | 'medium' | 'high' | 'critical';
  incidentsToday: number;
  blockedAttacks: number;
  complianceScore: number;
  encryptionCoverage: number;
}

interface AccessControl {
  id: string;
  resource: string;
  user: string;
  permission: 'read' | 'write' | 'execute' | 'admin';
  granted: boolean;
  source: 'role' | 'direct' | 'inherited';
  lastAccessed: Date;
  expiry?: Date;
}

interface NetworkSecurity {
  firewallStatus: 'active' | 'inactive' | 'warning';
  openPorts: number[];
  blockedIPs: string[];
  allowedIPs: string[];
  sslCertificates: SSLCertificate[];
  vpnConnections: number;
  intrusionAttempts: number;
}

interface SSLCertificate {
  domain: string;
  issuer: string;
  validFrom: Date;
  validTo: Date;
  status: 'valid' | 'expiring' | 'expired' | 'invalid';
  daysUntilExpiry: number;
}

const mockPolicies: SecurityPolicy[] = [
  {
