# Railway Production Environment Variables for Cival Dashboard
# Copy these variables to your Railway project environment settings

# Core Application Settings
NODE_ENV=production
PORT=3000
API_PORT=8000
NEXT_TELEMETRY_DISABLED=1

# Railway URLs (these will be auto-populated by Railway)
NEXT_PUBLIC_API_URL=${{RAILWAY_PUBLIC_DOMAIN}}:8000
NEXT_PUBLIC_WS_URL=wss://${{RAILWAY_PUBLIC_DOMAIN}}:8000

# Database Configuration (Supabase or Railway PostgreSQL)
DATABASE_URL=${{DATABASE_URL}}
SUPABASE_URL=${{SUPABASE_URL}}
SUPABASE_ANON_KEY=${{SUPABASE_ANON_KEY}}
SUPABASE_SERVICE_ROLE_KEY=${{SUPABASE_SERVICE_ROLE_KEY}}

# Redis Configuration (Railway Redis or external)
REDIS_URL=${{REDIS_URL}}
REDIS_HOST=${{REDIS_HOST}}
REDIS_PORT=${{REDIS_PORT}}
REDIS_PASSWORD=${{REDIS_PASSWORD}}

# AI and LLM API Keys
OPENAI_API_KEY=${{OPENAI_API_KEY}}
ANTHROPIC_API_KEY=${{ANTHROPIC_API_KEY}}
GOOGLE_API_KEY=${{GOOGLE_API_KEY}}

# Trading and Market Data APIs
ALPHA_VANTAGE_API_KEY=${{ALPHA_VANTAGE_API_KEY}}
POLYGON_API_KEY=${{POLYGON_API_KEY}}
FINNHUB_API_KEY=${{FINNHUB_API_KEY}}
BINANCE_API_KEY=${{BINANCE_API_KEY}}
BINANCE_SECRET_KEY=${{BINANCE_SECRET_KEY}}
COINBASE_API_KEY=${{COINBASE_API_KEY}}
COINBASE_SECRET=${{COINBASE_SECRET}}

# Web3 and Blockchain
ALCHEMY_API_KEY=${{ALCHEMY_API_KEY}}
INFURA_PROJECT_ID=${{INFURA_PROJECT_ID}}

# Memory System (Letta)
LETTA_SERVER_URL=${{LETTA_SERVER_URL}}
LETTA_API_KEY=${{LETTA_API_KEY}}

# Feature Flags
ENABLE_PAPER_TRADING=true
ENABLE_REAL_TRADING=false
ENABLE_BACKTESTING=true
ENABLE_MEMORY_SYSTEM=true
ENABLE_WEBSOCKETS=true

# Security Settings
JWT_SECRET=${{JWT_SECRET}}
CORS_ORIGINS=https://${{RAILWAY_PUBLIC_DOMAIN}}

# Logging and Monitoring
LOG_LEVEL=info
ENABLE_STRUCTURED_LOGGING=true
ENABLE_PERFORMANCE_MONITORING=true

# Python Service Configuration
PYTHONPATH=/app/python-ai-services
PYTHON_ENV=production

# FastAPI Configuration
FASTAPI_TITLE="Cival Dashboard API"
FASTAPI_VERSION="2.0.0"
FASTAPI_DEBUG=false

# Application Limits
MAX_PORTFOLIO_SIZE=1000000
MAX_CONCURRENT_TRADES=50
MAX_MEMORY_ENTRIES=10000

# External Integration Settings
WEBHOOK_SECRET=${{WEBHOOK_SECRET}}
API_RATE_LIMIT=1000
WEBSOCKET_MAX_CONNECTIONS=100

