    },
    {
      id: 'advanced',
      label: 'Advanced',
      icon: <Settings className="h-4 w-4" />,
      component: <AdvancedTab />
    }
  ]

  if (isLoading) {
    return <LoadingScreen />
  }

  return (
    <AGUIProvider>
      <div className="min-h-screen bg-background">
        {/* Mobile Menu */}
        <AnimatePresence>
          {isMobileMenuOpen && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
