  1	/**
  2	 * AG-UI Protocol Event Transport Layer
  3	 * Phase 8: WebSocket/SSE transport for real-time communication
  4	 */
  5	
  6	import { io, Socket } from 'socket.io-client';
  7	import { AllEvents } from './ag-ui-protocol-v2';
  8	import { agUIConfig } from './ag-ui-config';
  9	
 10	export type EventCallback<T = any> = (data: T) => void;
 11	export type EventType = keyof AllEvents;
 12	
 13	export interface EventSubscription {
 14	  id: string;
 15	  eventType: EventType;
 16	  callback: EventCallback;
 17	  options?: SubscriptionOptions;
 18	}
 19	
 20	export interface SubscriptionOptions {
 21	  priority?: 'high' | 'medium' | 'low';
 22	  once?: boolean;
 23	  filter?: (data: any) => boolean;
 24	  transform?: (data: any) => any;
 25	}
 26	
 27	export interface EventTransportMetrics {
 28	  connected: boolean;
 29	  connectionTime?: Date;
 30	  lastHeartbeat?: Date;
 31	  eventsReceived: number;
 32	  eventsSent: number;
 33	  errors: number;
 34	  reconnectCount: number;
 35	  averageLatency: number;
 36	  queueSize: number;
 37	}
 38	
 39	export class EventTransportLayer {
 40	  private socket: Socket | null = null;
 41	  private subscriptions = new Map<string, EventSubscription>();
 42	  private eventQueue: Array<{ type: EventType; data: any; timestamp: Date }> = [];
 43	  private metrics: EventTransportMetrics = {
 44	    connected: false,
 45	    eventsReceived: 0,
 46	    eventsSent: 0,
 47	    errors: 0,
 48	    reconnectCount: 0,
 49	    averageLatency: 0,
 50	    queueSize: 0
 51	  };
 52	  
 53	  private reconnectTimer?: NodeJS.Timeout;
 54	  private heartbeatTimer?: NodeJS.Timeout;
 55	  private latencyTracker = new Map<string, number>();
 56	  
 57	  constructor() {
 58	    this.initialize();
 59	  }
 60	  
 61	  /**
 62	   * Initialize the event transport layer
 63	   */
 64	  private initialize(): void {
 65	    try {
 66	      const config = agUIConfig.connection.websocket;
 67	      
 68	      this.socket = io(config.url, {
 69	        transports: ['websocket'],
 70	        reconnection: config.reconnect,
 71	        reconnectionAttempts: config.maxReconnectAttempts,
 72	        reconnectionDelay: config.reconnectInterval,
 73	        timeout: 10000,
 74	        auth: {
 75	          token: this.getAuthToken()
 76	        }
 77	      });
 78	      
 79	      this.setupEventHandlers();
 80	      this.startHeartbeat();
 81	      
 82	      console.log('ğŸš€ Event transport layer initialized');
 83	    } catch (error) {
 84	      console.error('âŒ Failed to initialize event transport:', error);
 85	      this.handleError(error as Error);
 86	    }
 87	  }
 88	  
 89	  /**
 90	   * Set up WebSocket event handlers
 91	   */
 92	  private setupEventHandlers(): void {
 93	    if (!this.socket) return;
 94	    
 95	    // Connection events
 96	    this.socket.on('connect', () => {
 97	      this.metrics.connected = true;
 98	      this.metrics.connectionTime = new Date();
 99	      console.log('ğŸŸ¢ WebSocket connected to trading platform');
100	      
101	      // Flush queued events
102	      this.flushEventQueue();
103	      
104	      // Notify AG-UI config
105	      agUIConfig.events.onConnect?.();
106	    });
107	    
108	    this.socket.on('disconnect', (reason) => {
109	      this.metrics.connected = false;
110	      console.log('ğŸ”´ WebSocket disconnected:', reason);
111	      
112	      // Notify AG-UI config
113	      agUIConfig.events.onDisconnect?.();
114	      
115	      // Attempt reconnection if needed
116	      if (reason === 'io server disconnect') {
117	        this.scheduleReconnect();
118	      }
119	    });
120	    
121	    this.socket.on('reconnect', (attemptNumber) => {
122	      this.metrics.reconnectCount++;
123	      console.log(`ğŸ”„ WebSocket reconnected after ${attemptNumber} attempts`);
124	      
125	      // Notify AG-UI config
126	      agUIConfig.events.onReconnect?.();
127	    });
128	    
129	    this.socket.on('connect_error', (error) => {
130	      this.metrics.errors++;
131	      console.error('âŒ WebSocket connection error:', error);
132	      this.handleError(error);
133	    });
134	    
135	    // Heartbeat/pong response
136	    this.socket.on('pong', () => {
137	      this.metrics.lastHeartbeat = new Date();
138	    });
139	    
140	    // Trading platform events
141	    this.setupTradingEventHandlers();
142	  }
143	  
144	  /**
145	   * Set up handlers for trading platform specific events
146	   */
147	  private setupTradingEventHandlers(): void {
148	    if (!this.socket) return;
149	    
150	    // Goal management events
151	    this.socket.on('goal.created', (data) => this.handleIncomingEvent('goal.created', data));
152	    this.socket.on('goal.analyzed', (data) => this.handleIncomingEvent('goal.analyzed', data));
153	    this.socket.on('goal.progress_updated', (data) => this.handleIncomingEvent('goal.progress_updated', data));
154	    this.socket.on('goal.completed', (data) => this.handleIncomingEvent('goal.completed', data));
155	    this.socket.on('goal.optimization_suggested', (data) => this.handleIncomingEvent('goal.optimization_suggested', data));
156	    this.socket.on('goal.decomposed', (data) => this.handleIncomingEvent('goal.decomposed', data));
157	    this.socket.on('goal.cancelled', (data) => this.handleIncomingEvent('goal.cancelled', data));
158	    
159	    // Analytics events
160	    this.socket.on('analytics.report_generated', (data) => this.handleIncomingEvent('analytics.report_generated', data));
161	    this.socket.on('prediction.completed', (data) => this.handleIncomingEvent('prediction.completed', data));
162	    this.socket.on('pattern.identified', (data) => this.handleIncomingEvent('pattern.identified', data));
163	    this.socket.on('recommendation.created', (data) => this.handleIncomingEvent('recommendation.created', data));
164	    
165	    // System events
166	    this.socket.on('system.health_check', (data) => this.handleIncomingEvent('system.health_check', data));
167	    this.socket.on('system.error', (data) => this.handleIncomingEvent('system.error', data));
168	    this.socket.on('system.notification', (data) => this.handleIncomingEvent('system.notification', data));
169	    
170	    // Future event types (wallet, agent, trading)
171	    this.socket.on('wallet.created', (data) => this.handleIncomingEvent('wallet.created', data));
172	    this.socket.on('wallet.updated', (data) => this.handleIncomingEvent('wallet.updated', data));
173	    this.socket.on('wallet.balance_changed', (data) => this.handleIncomingEvent('wallet.balance_changed', data));
174	    this.socket.on('wallet.transaction', (data) => this.handleIncomingEvent('wallet.transaction', data));
175	    
176	    this.socket.on('agent.decision', (data) => this.handleIncomingEvent('agent.decision', data));
177	    this.socket.on('agent.communication', (data) => this.handleIncomingEvent('agent.communication', data));
178	    this.socket.on('agent.performance_update', (data) => this.handleIncomingEvent('agent.performance_update', data));
179	    this.socket.on('agent.strategy_change', (data) => this.handleIncomingEvent('agent.strategy_change', data));
180	    
181	    this.socket.on('trade.executed', (data) => this.handleIncomingEvent('trade.executed', data));
182	    this.socket.on('trade.signal_generated', (data) => this.handleIncomingEvent('trade.signal_generated', data));
183	    this.socket.on('trade.risk_alert', (data) => this.handleIncomingEvent('trade.risk_alert', data));
184	    this.socket.on('trade.position_update', (data) => this.handleIncomingEvent('trade.position_update', data));
185	  }
186	  
187	  /**
188	   * Handle incoming events from the trading platform
189	   */
190	  private handleIncomingEvent(eventType: EventType, data: any): void {
191	    try {
192	      this.metrics.eventsReceived++;
193	      
194	      // Calculate latency if timestamp is available
195	      if (data.timestamp) {
196	        const latency = Date.now() - new Date(data.timestamp).getTime();
197	        this.updateLatencyMetrics(latency);
198	      }
199	      
200	      // Find all subscriptions for this event type
201	      const relevantSubscriptions = Array.from(this.subscriptions.values())
202	        .filter(sub => sub.eventType === eventType);
203	      
204	      for (const subscription of relevantSubscriptions) {
205	        try {
206	          // Apply filter if specified
207	          if (subscription.options?.filter && !subscription.options.filter(data)) {
208	            continue;
209	          }
210	          
211	          // Apply transformation if specified
212	          let processedData = data;
213	          if (subscription.options?.transform) {
214	            processedData = subscription.options.transform(data);
215	          }
216	          
217	          // Call the subscription callback
218	          subscription.callback(processedData);
219	          
220	          // Remove one-time subscriptions
221	          if (subscription.options?.once) {
222	            this.subscriptions.delete(subscription.id);
223	          }
224	          
225	        } catch (error) {
226	          console.error(`âŒ Error in event subscription ${subscription.id}:`, error);
227	          this.metrics.errors++;
228	        }
229	      }
230	      
231	      // Log high-priority events
232	      if (this.getEventPriority(eventType) === 'high') {
233	        console.log(`ğŸ”” High-priority event received: ${eventType}`, data);
234	      }
235	      
236	    } catch (error) {
237	      console.error('âŒ Error handling incoming event:', error);
238	      this.metrics.errors++;
239	    }
240	  }
241	  
242	  /**
243	   * Subscribe to events
244	   */
245	  public subscribe<T = any>(
246	    eventType: EventType,
247	    callback: EventCallback<T>,
248	    options?: SubscriptionOptions
249	  ): string {
250	    const subscriptionId = `sub_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
251	    
252	    const subscription: EventSubscription = {
253	      id: subscriptionId,
254	      eventType,
255	      callback: callback as EventCallback,
256	      options
257	    };
258	    
259	    this.subscriptions.set(subscriptionId, subscription);
260	    
261	    console.log(`ğŸ“¡ Subscribed to ${eventType} with ID: ${subscriptionId}`);
262	    
263	    return subscriptionId;
264	  }
265	  
266	  /**
267	   * Unsubscribe from events
268	   */
269	  public unsubscribe(subscriptionId: string): boolean {
270	    const removed = this.subscriptions.delete(subscriptionId);
271	    
272	    if (removed) {
273	      console.log(`ğŸ“¡ Unsubscribed from event with ID: ${subscriptionId}`);
274	    }
275	    
276	    return removed;
277	  }
278	  
279	  /**
280	   * Emit events to the trading platform
281	   */
282	  public emit(eventType: EventType, data: any): void {
283	    try {
284	      if (!this.socket || !this.metrics.connected) {
285	        // Queue event if not connected
286	        this.queueEvent(eventType, data);
287	        return;
288	      }
289	      
290	      // Add timestamp for latency tracking
291	      const eventData = {
292	        ...data,
293	        timestamp: new Date().toISOString(),
294	        clientId: this.getClientId()
295	      };
296	      
297	      this.socket.emit(eventType, eventData);
298	      this.metrics.eventsSent++;
299	      
300	      console.log(`ğŸ“¤ Emitted event: ${eventType}`);
301	      
302	    } catch (error) {
303	      console.error('âŒ Error emitting event:', error);
304	      this.metrics.errors++;
305	      
306	      // Queue event for retry
307	      this.queueEvent(eventType, data);
308	    }
309	  }
310	  
311	  /**
312	   * Queue events when disconnected
313	   */
314	  private queueEvent(eventType: EventType, data: any): void {
315	    const queuedEvent = {
316	      type: eventType,
317	      data,
318	      timestamp: new Date()
319	    };
320	    
321	    this.eventQueue.push(queuedEvent);
322	    this.metrics.queueSize = this.eventQueue.length;
323	    
324	    // Limit queue size
325	    const maxQueueSize = agUIConfig.transport.buffer.maxSize || 1000;
326	    if (this.eventQueue.length > maxQueueSize) {
327	      this.eventQueue.shift(); // Remove oldest event
328	    }
329	    
330	    console.log(`ğŸ“¦ Queued event: ${eventType} (queue size: ${this.eventQueue.length})`);
331	  }
332	  
333	  /**
334	   * Flush queued events when reconnected
335	   */
336	  private flushEventQueue(): void {
337	    if (!agUIConfig.transport.buffer.flushOnReconnect) {
338	      return;
339	    }
340	    
341	    console.log(`ğŸ“¤ Flushing ${this.eventQueue.length} queued events`);
342	    
343	    const eventsToFlush = [...this.eventQueue];
344	    this.eventQueue = [];
345	    this.metrics.queueSize = 0;
346	    
347	    for (const event of eventsToFlush) {
348	      // Add age information
349	      const age = Date.now() - event.timestamp.getTime();
350	      const eventData = {
351	        ...event.data,
352	        _queued: true,
353	        _age: age
354	      };
355	      
356	      this.emit(event.type, eventData);
357	    }
358	  }
359	  
360	  /**
361	   * Start heartbeat mechanism
362	   */
363	  private startHeartbeat(): void {
364	    const heartbeatInterval = agUIConfig.connection.websocket.heartbeat?.interval || 30000;
365	    
366	    this.heartbeatTimer = setInterval(() => {
367	      if (this.socket && this.metrics.connected) {
368	        this.socket.emit('ping');
369	      }
370	    }, heartbeatInterval);
371	  }
372	  
373	  /**
374	   * Schedule reconnection attempt
375	   */
376	  private scheduleReconnect(): void {
377	    if (this.reconnectTimer) {
378	      clearTimeout(this.reconnectTimer);
379	    }
380	    
381	    const reconnectInterval = agUIConfig.connection.websocket.reconnectInterval || 5000;
382	    
383	    this.reconnectTimer = setTimeout(() => {
384	      console.log('ğŸ”„ Attempting to reconnect...');
385	      this.socket?.connect();
386	    }, reconnectInterval);
387	  }
388	  
389	  /**
390	   * Handle transport errors
391	   */
392	  private handleError(error: Error): void {
393	    this.metrics.errors++;
394	    
395	    console.error('âŒ Event transport error:', error);
396	    
397	    // Notify AG-UI config
398	    agUIConfig.events.onError?.(error);
399	    
400	    // Emit system error event for UI notification
401	    if (this.metrics.connected) {
402	      this.emit('system.error', {
403	        service: 'event_transport',
404	        error_type: error.name,
405	        message: error.message,
406	        timestamp: new Date().toISOString()
407	      });
408	    }
409	  }
410	  
411	  /**
412	   * Update latency metrics
413	   */
414	  private updateLatencyMetrics(latency: number): void {
415	    const trackingId = Date.now().toString();
416	    this.latencyTracker.set(trackingId, latency);
417	    
418	    // Keep only last 100 latency measurements
419	    if (this.latencyTracker.size > 100) {
420	      const oldestKey = Array.from(this.latencyTracker.keys())[0];
421	      this.latencyTracker.delete(oldestKey);
422	    }
423	    
424	    // Calculate average latency
425	    const latencies = Array.from(this.latencyTracker.values());
426	    this.metrics.averageLatency = latencies.reduce((sum, lat) => sum + lat, 0) / latencies.length;
427	  }
428	  
429	  /**
430	   * Get event priority
431	   */
432	  private getEventPriority(eventType: EventType): 'high' | 'medium' | 'low' {
433	    const highPriorityEvents = [
434	      'goal.completed',
435	      'trade.executed',
436	      'system.error',
437	      'agent.decision',
438	      'trade.risk_alert'
439	    ];
440	    
441	    const mediumPriorityEvents = [
442	      'goal.created',
443	      'goal.progress_updated',
444	      'wallet.transaction',
445	      'prediction.completed',
446	      'agent.performance_update'
447	    ];
448	    
449	    if (highPriorityEvents.includes(eventType as any)) {
450	      return 'high';
451	    } else if (mediumPriorityEvents.includes(eventType as any)) {
452	      return 'medium';
453	    } else {
454	      return 'low';
455	    }
456	  }
457	  
458	  /**
459	   * Get authentication token
460	   */
461	  private getAuthToken(): string | undefined {
462	    try {
463	      return localStorage.getItem(agUIConfig.connection.auth?.tokenKey || 'authToken') || undefined;
464	    } catch (error) {
465	      console.warn('âš ï¸ Could not retrieve auth token:', error);
466	      return undefined;
467	    }
468	  }
469	  
470	  /**
471	   * Get client ID for event tracking
472	   */
473	  private getClientId(): string {
474	    try {
475	      let clientId = localStorage.getItem('clientId');
476	      if (!clientId) {
477	        clientId = `client_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
478	        localStorage.setItem('clientId', clientId);
479	      }
480	      return clientId;
481	    } catch (error) {
482	      return `client_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
483	    }
484	  }
485	  
486	  /**
487	   * Get transport metrics
488	   */
489	  public getMetrics(): EventTransportMetrics {
490	    return { ...this.metrics };
491	  }
492	  
493	  /**
494	   * Get connection status
495	   */
496	  public isConnected(): boolean {
497	    return this.metrics.connected;
498	  }
499	  
500	  /**
501	   * Get subscription count
502	   */
503	  public getSubscriptionCount(): number {
504	    return this.subscriptions.size;
505	  }
506	  
507	  /**
508	   * Force reconnection
509	   */
510	  public reconnect(): void {
511	    console.log('ğŸ”„ Forcing reconnection...');
512	    this.socket?.disconnect();
513	    this.socket?.connect();
514	  }
515	  
516	  /**
517	   * Cleanup resources
518	   */
519	  public destroy(): void {
520	    console.log('ğŸ—‘ï¸ Destroying event transport layer');
521	    
522	    // Clear timers
523	    if (this.reconnectTimer) {
524	      clearTimeout(this.reconnectTimer);
525	    }
526	    if (this.heartbeatTimer) {
527	      clearInterval(this.heartbeatTimer);
528	    }
529	    
530	    // Clear subscriptions
531	    this.subscriptions.clear();
532	    
533	    // Clear queue
534	    this.eventQueue = [];
535	    
536	    // Disconnect socket
537	    if (this.socket) {
538	      this.socket.disconnect();
539	      this.socket = null;
540	    }
541	    
542	    // Reset metrics
543	    this.metrics = {
544	      connected: false,
545	      eventsReceived: 0,
546	      eventsSent: 0,
547	      errors: 0,
548	      reconnectCount: 0,
549	      averageLatency: 0,
550	      queueSize: 0
551	    };
552	  }
553	}
554	
555	// Create singleton instance
556	export const eventTransport = new EventTransportLayer();
557	
558	// Export convenience functions
559	export const subscribe = eventTransport.subscribe.bind(eventTransport);
560	export const unsubscribe = eventTransport.unsubscribe.bind(eventTransport);
561	export const emit = eventTransport.emit.bind(eventTransport);
562	export const getMetrics = eventTransport.getMetrics.bind(eventTransport);
563	export const isConnected = eventTransport.isConnected.bind(eventTransport);
564	
565	export default eventTransport;

