/**
 * System Overview Dashboard Component
 * Complete integration of all phases: Comprehensive AI-powered trading system
 */

import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Activity,
  TrendingUp,
  Shield,
  Brain,
  Users,
  Globe,
  Zap,
  Target,
  AlertTriangle,
  CheckCircle,
  BarChart3,
  DollarSign,
  Cpu,
  RefreshCw,
  Settings,
  Eye,
  Maximize2
} from 'lucide-react';

import { getAGUIEventBus } from '../../ag-ui-setup/ag-ui-protocol-v2';
import { RealTimeDashboard } from '../real-time-dashboard/RealTimeDashboard';
import { LLMAnalyticsDashboard } from '../llm-analytics/LLMAnalyticsDashboard';

interface SystemStatus {
  overall: 'healthy' | 'degraded' | 'critical';
  services: {
    [serviceName: string]: {
      status: 'running' | 'stopped' | 'error';
      health: 'healthy' | 'degraded' | 'critical';
      uptime: number;
      lastCheck: string;
    };
  };
  performance: {
    totalRequests: number;
    avgResponseTime: number;
    errorRate: number;
    throughput: number;
  };
}

interface TradingMetrics {
  totalPortfolioValue: number;
  dailyPnL: number;
  totalTrades: number;
  winRate: number;
  activeAgents: number;
  riskLevel: 'low' | 'medium' | 'high' | 'critical';
  arbitrageOpportunities: number;
  exchangesConnected: number;
}

interface SystemOverviewDashboardProps {
  className?: string;
}

export const SystemOverviewDashboard: React.FC<SystemOverviewDashboardProps> = ({
  className = ''
}) => {
  const [systemStatus, setSystemStatus] = useState<SystemStatus | null>(null);
  const [tradingMetrics, setTradingMetrics] = useState<TradingMetrics | null>(null);
  const [selectedView, setSelectedView] = useState<'overview' | 'realtime' | 'analytics'>('overview');
  const [isLoading, setIsLoading] = useState(true);
  const [lastUpdate, setLastUpdate] = useState<Date>(new Date());

  const eventBus = getAGUIEventBus();

  useEffect(() => {
    const loadSystemData = async () => {
      setIsLoading(true);
      try {
        // Load mock system status
        const mockSystemStatus: SystemStatus = {
          overall: 'healthy',
          services: {
            'Autonomous Agent Coordinator': {
              status: 'running',
              health: 'healthy',
              uptime: 99.8,
              lastCheck: new Date().toISOString()
            },
            'LLM Integration Service': {
              status: 'running',
              health: 'healthy',
              uptime: 99.5,
              lastCheck: new Date().toISOString()
            },
            'Advanced Trading Orchestrator': {
              status: 'running',
              health: 'healthy',
              uptime: 99.9,
              lastCheck: new Date().toISOString()
            },
            'Multi-Exchange Integration': {
              status: 'running',
              health: 'healthy',
              uptime: 98.7,
              lastCheck: new Date().toISOString()
            },
            'Advanced Risk Management': {
              status: 'running',
              health: 'healthy',
              uptime: 99.6,
              lastCheck: new Date().toISOString()
            },
            'Master Wallet Service': {
              status: 'running',
              health: 'healthy',
              uptime: 99.4,
              lastCheck: new Date().toISOString()
            },
            'Intelligent Goal Service': {
              status: 'running',
              health: 'healthy',
              uptime: 99.2,
              lastCheck: new Date().toISOString()
            },
            'AG-UI Protocol v2': {
              status: 'running',
              health: 'healthy',
              uptime: 99.7,
              lastCheck: new Date().toISOString()
            }
          },
          performance: {
            totalRequests: 1247892,
            avgResponseTime: 156,
            errorRate: 0.12,
            throughput: 2847
          }
        };

        const mockTradingMetrics: TradingMetrics = {
          totalPortfolioValue: 52346.25,
          dailyPnL: 1346.50,
          totalTrades: 2847,
          winRate: 68.4,
          activeAgents: 4,
          riskLevel: 'low',
          arbitrageOpportunities: 12,
          exchangesConnected: 3
        };

        setSystemStatus(mockSystemStatus);
        setTradingMetrics(mockTradingMetrics);
        setLastUpdate(new Date());
      } finally {
        setIsLoading(false);
      }
    };

    loadSystemData();

    // Set up auto-refresh
    const interval = setInterval(() => {
      loadSystemData();
    }, 30000); // Refresh every 30 seconds

    return () => clearInterval(interval);
  }, []);

  // Subscribe to system events
  useEffect(() => {
    const subscriptions = [
