 79	      validationErrors.push(`Position size would exceed max ${agent.maxPositionSize}`);
 80	    }
 81	    
 82	    if (validationErrors.length > 0) {
 83	      return NextResponse.json({ 
 84	        error: 'Trade validation failed', 
 85	        details: validationErrors 
