117	        status: result.status,
118	        exchange,
119	        executedAt: new Date(),
120	        createdAt: new Date()
121	      };
122	      
123	      // Store trade (in-memory for demo)
124	      if (!agentTrades.has(agentId)) {
125	        agentTrades.set(agentId, []);
126	      }
127	      agentTrades.get(agentId).push(trade);
128	      
129	      // Update agent stats
