150	      return NextResponse.json({ 
151	        error: 'Failed to execute trade',
152	        details: error.message 
153	      }, { status: 500 });
154	    }
155	    
156	  } catch (error) {
157	    console.error('Agent trading error:', error);
158	    return NextResponse.json({ 
159	      error: 'Internal server error' 
160	    }, { status: 500 });
161	  }
