 79	export class TradingManager extends EventEmitter {
 80	  private connectors: Map<string, BaseExchangeConnector> = new Map();
 81	  private config: TradingManagerConfig;
 82	  private marketDataCache: Map<string, Map<string, MarketData>> = new Map(); // symbol -> exchange -> data
 83	  private orderBookCache: Map<string, Map<string, OrderBook>> = new Map();
 84	  private isInitialized = false;
