 93	
 94	  private initializeExchanges(): void {
 95	    for (const [name, exchangeConfig] of Object.entries(this.config.exchanges)) {
 96	      if (!exchangeConfig.enabled) continue;
 97	
 98	      let connector: BaseExchangeConnector;
 99	
100	      switch (exchangeConfig.type) {
101	        case 'hyperliquid':
102	          connector = new HyperliquidConnector(exchangeConfig.credentials);
103	          break;
104	        case 'uniswap':
105	          connector = new UniswapConnector(exchangeConfig.credentials);
106	          break;
107	        case 'vertex':
108	          // TODO: Implement Vertex connector
109	          console.warn(`Vertex connector not implemented yet`);
110	          continue;
111	        case 'bluefin':
112	          // TODO: Implement Bluefin connector
113	          console.warn(`Bluefin connector not implemented yet`);
114	          continue;
115	        case 'bybit':
116	          // TODO: Implement Bybit connector
117	          console.warn(`Bybit connector not implemented yet`);
118	          continue;
119	        default:
120	          console.error(`Unknown exchange type: ${exchangeConfig.type}`);
121	          continue;
122	      }
123	
124	      // Set up event handlers
125	      connector.onMarketDataUpdate = (data: MarketData) => {
126	        this.handleMarketDataUpdate(name, data);
127	      };
128	
129	      connector.onOrderBookUpdate = (data: OrderBook) => {
130	        this.handleOrderBookUpdate(name, data);
131	      };
132	
133	      connector.onOrderUpdate = (data: Order) => {
134	        this.handleOrderUpdate(name, data);
135	      };
136	
137	      connector.onPositionUpdate = (data: Position) => {
138	        this.handlePositionUpdate(name, data);
139	      };
140	
141	      connector.onError = (error: Error) => {
142	        this.emit('exchangeError', { exchange: name, error });
143	      };
144	
145	      this.connectors.set(name, connector);
146	    }
147	  }
