215	  private async updateMarketData(): Promise<void> {
216	    const symbols = ['BTC', 'ETH', 'SOL', 'USDC', 'USDT'];
217	    
218	    for (const symbol of symbols) {
219	      const exchangeData: Record<string, MarketData> = {};
220	      
221	      for (const [name, connector] of this.connectors) {
222	        try {
223	          const data = await connector.getMarketData(symbol);
224	          exchangeData[name] = data;
225	          
226	          // Cache the data
227	          if (!this.marketDataCache.has(symbol)) {
228	            this.marketDataCache.set(symbol, new Map());
229	          }
230	          this.marketDataCache.get(symbol)!.set(name, data);
231	        } catch (error) {
232	          // Silently ignore errors for individual exchanges
233	        }
234	      }
235	
236	      // Emit aggregated market data
237	      if (Object.keys(exchangeData).length > 0) {
238	        const aggregated = this.aggregateMarketData(symbol, exchangeData);
239	        this.emit('marketDataUpdate', aggregated);
240	      }
241	    }
242	  }
