244	  private aggregateMarketData(symbol: string, exchangeData: Record<string, MarketData>): AggregatedMarketData {
245	    const exchanges = Object.keys(exchangeData);
246	    const prices = Object.values(exchangeData).map(d => d.price);
247	    const volumes = Object.values(exchangeData).map(d => d.volume);
248	    const bids = Object.entries(exchangeData).map(([ex, data]) => ({ price: data.bid, exchange: ex }));
249	    const asks = Object.entries(exchangeData).map(([ex, data]) => ({ price: data.ask, exchange: ex }));
250	
251	    const bestBid = bids.reduce((max, current) => current.price > max.price ? current : max);
252	    const bestAsk = asks.reduce((min, current) => current.price < min.price ? current : min);
253	
254	    const avgPrice = prices.reduce((sum, price) => sum + price, 0) / prices.length;
255	    const totalVolume = volumes.reduce((sum, vol) => sum + vol, 0);
256	
257	    return {
258	      symbol,
259	      price: avgPrice,
260	      volume: totalVolume,
261	      high24h: Math.max(...Object.values(exchangeData).map(d => d.high24h)),
262	      low24h: Math.min(...Object.values(exchangeData).map(d => d.low24h)),
263	      change24h: Object.values(exchangeData)[0]?.change24h || 0,
264	      changePercent24h: Object.values(exchangeData)[0]?.changePercent24h || 0,
265	      timestamp: Date.now(),
266	      bid: bestBid.price,
267	      ask: bestAsk.price,
268	      spread: bestAsk.price - bestBid.price,
269	      exchanges,
270	      bestBid,
271	      bestAsk,
272	      totalVolume,
273	    };
274	  }
