366	  async getPortfolioSummary(): Promise<PortfolioSummary> {
367	    const balancesByExchange: Record<string, Balance[]> = {};
368	    const positionsByExchange: Record<string, Position[]> = {};
369	    const allOrders: Order[] = [];
370	
371	    // Collect data from all exchanges
372	    for (const [name, connector] of this.connectors) {
373	      try {
374	        const [balances, positions, orders] = await Promise.all([
375	          connector.getBalances(),
376	          connector.getPositions(),
377	          connector.getOrders(),
378	        ]);
379	
380	        balancesByExchange[name] = balances;
381	        positionsByExchange[name] = positions;
382	        allOrders.push(...orders.map(order => ({ ...order, exchange: name } as any)));
383	      } catch (error) {
384	        console.error(`Failed to get portfolio data from ${name}:`, error);
385	      }
386	    }
387	
388	    // Calculate total portfolio value
389	    let totalValue = 0;
390	    let totalPnL = 0;
391	
392	    for (const balances of Object.values(balancesByExchange)) {
393	      for (const balance of balances) {
394	        totalValue += balance.usdValue || 0;
395	      }
396	    }
397	
398	    for (const positions of Object.values(positionsByExchange)) {
399	      for (const position of positions) {
400	        totalPnL += position.unrealizedPnl + position.realizedPnl;
401	      }
402	    }
403	
404	    const totalPnLPercent = totalValue > 0 ? (totalPnL / totalValue) * 100 : 0;
405	
406	    return {
407	      totalValue,
408	      totalPnL,
409	      totalPnLPercent,
410	      balancesByExchange,
411	      positionsByExchange,
412	      activeOrders: allOrders.filter(order => order.status === 'open' || order.status === 'pending'),
413	      strategies: Array.from(this.strategies.values()),
414	    };
415	  }
