417	  async placeOrder(trade: Trade, exchange?: string): Promise<Order> {
418	    const targetExchange = exchange || this.config.defaultExchange;
419	    const connector = this.connectors.get(targetExchange);
420	    
421	    if (!connector) {
422	      throw new Error(`Exchange ${targetExchange} not found`);
423	    }
424	
425	    // Apply risk management checks
426	    await this.validateTrade(trade, targetExchange);
427	
428	    try {
429	      const order = await connector.placeOrder(trade);
430	      this.emit('orderPlaced', { exchange: targetExchange, order });
