438	  private async validateTrade(trade: Trade, exchange: string): Promise<void> {
439	    const { riskManagement } = this.config;
440	    
441	    // Check position size limits
442	    const positions = await this.connectors.get(exchange)!.getPositions();
443	    const existingPosition = positions.find(p => p.symbol === trade.symbol);
444	    const newSize = (existingPosition?.size || 0) + trade.quantity;
445	    
446	    if (newSize > riskManagement.maxPositionSize) {
447	      throw new Error(`Position size ${newSize} exceeds maximum allowed ${riskManagement.maxPositionSize}`);
448	    }
449	
450	    // Check daily loss limits
451	    const summary = await this.getPortfolioSummary();
452	    if (summary.totalPnL < -riskManagement.maxDailyLoss) {
453	      throw new Error(`Daily loss limit exceeded: ${summary.totalPnL}`);
454	    }
455	
456	    // Additional risk checks can be added here
457	  }
