503	  // Arbitrage opportunities
504	  async findArbitrageOpportunities(symbol: string): Promise<Array<{
505	    symbol: string;
506	    buyExchange: string;
507	    sellExchange: string;
508	    buyPrice: number;
509	    sellPrice: number;
510	    profit: number;
511	    profitPercent: number;
512	  }>> {
513	    const opportunities: any[] = [];
514	    const symbolCache = this.marketDataCache.get(symbol);
515	    
516	    if (!symbolCache || symbolCache.size < 2) {
517	      return opportunities;
518	    }
519	
520	    const exchanges = Array.from(symbolCache.entries());
521	    
522	    for (let i = 0; i < exchanges.length; i++) {
523	      for (let j = i + 1; j < exchanges.length; j++) {
524	        const [exchange1, data1] = exchanges[i];
525	        const [exchange2, data2] = exchanges[j];
526	        
527	        const spread1to2 = data2.bid - data1.ask;
528	        const spread2to1 = data1.bid - data2.ask;
529	        
530	        if (spread1to2 > 0) {
531	          const profitPercent = (spread1to2 / data1.ask) * 100;
532	          if (profitPercent > 0.1) { // Minimum 0.1% profit
533	            opportunities.push({
534	              symbol,
535	              buyExchange: exchange1,
536	              sellExchange: exchange2,
537	              buyPrice: data1.ask,
538	              sellPrice: data2.bid,
539	              profit: spread1to2,
540	              profitPercent,
541	            });
542	          }
543	        }
544	        
545	        if (spread2to1 > 0) {
546	          const profitPercent = (spread2to1 / data2.ask) * 100;
547	          if (profitPercent > 0.1) { // Minimum 0.1% profit
548	            opportunities.push({
549	              symbol,
550	              buyExchange: exchange2,
551	              sellExchange: exchange1,
552	              buyPrice: data2.ask,
553	              sellPrice: data1.bid,
554	              profit: spread2to1,
555	              profitPercent,
556	            });
557	          }
558	        }
559	      }
560	    }
561	    
562	    return opportunities.sort((a, b) => b.profitPercent - a.profitPercent);
563	  }
