143	          { status: 404 }
144	        );
145	      }
146	      return NextResponse.json({ agent });
147	    }
148	
149	    // Return all agents with filtering options
150	    const framework = searchParams.get('framework');
151	    const specialization = searchParams.get('specialization');
152	    const capabilities = searchParams.get('capabilities');
153	
154	    let agents = Array.from(registeredAgents.values());
155	
156	    // Apply filters
157	    if (framework) {
158	      agents = agents.filter(agent => agent.metadata.framework === framework);
159	    }
160	    if (specialization) {
161	      agents = agents.filter(agent =>
162	        agent.metadata.specialization.includes(specialization)
163	      );
164	    }
165	    if (capabilities) {
166	      agents = agents.filter(agent =>
167	        agent.capabilities.includes(capabilities)
168	      );
169	    }
170	
171	    return NextResponse.json({
172	      agents,
173	      total: agents.length,
174	      frameworks: ['ADK', 'CrewAI', 'LangGraph', 'LlamaIndex'],
