  1	/**
  2	 * React Hooks for Backend API Integration
  3	 * Provides easy-to-use hooks for connecting React components to the backend
  4	 */
  5	
  6	import { useState, useEffect, useCallback, useRef } from 'react';
  7	import { 
  8	  backendApi, 
  9	  ApiResponse, 
 10	  PortfolioSummary, 
 11	  Position, 
 12	  TradingSignal, 
 13	  AgentStatus, 
 14	  MarketOverview, 
 15	  PerformanceMetrics,
 16	  HealthCheck 
 17	} from '@/lib/api/backend-client';
 18	
 19	// Generic hook for API calls with loading and error states
 20	export function useApiCall<T>(
 21	  apiCall: () => Promise<ApiResponse<T>>,
 22	  dependencies: any[] = [],
 23	  options: {
 24	    immediate?: boolean;
 25	    refreshInterval?: number;
 26	    retryOnError?: boolean;
 27	    maxRetries?: number;
 28	  } = {}
 29	) {
 30	  const [data, setData] = useState<T | null>(null);
 31	  const [loading, setLoading] = useState(false);
 32	  const [error, setError] = useState<string | null>(null);
 33	  const [lastUpdated, setLastUpdated] = useState<Date | null>(null);
 34	  const [retryCount, setRetryCount] = useState(0);
 35	  const intervalRef = useRef<NodeJS.Timeout | null>(null);
 36	  const retryTimeoutRef = useRef<NodeJS.Timeout | null>(null);
 37	
 38	  const maxRetries = options.maxRetries || 3;
 39	  const retryOnError = options.retryOnError !== false;
 40	
 41	  const fetchData = useCallback(async (isRetry = false) => {
 42	    try {
 43	      if (!isRetry) {
 44	        setLoading(true);
 45	        setError(null);
