114	class BackendApiClient {
115	  private baseUrl: string;
116	  private timeout: number;
117	  private authToken: string | null = null;
118	
119	  constructor() {
120	    // Auto-detect backend URL based on environment
121	    this.baseUrl = process.env.NEXT_PUBLIC_BACKEND_URL || 
122	                   process.env.BACKEND_URL || 
123	                   'http://localhost:8000';
124	    this.timeout = 10000; // 10 second timeout
125	    
126	    // Load auth token from localStorage
127	    if (typeof window !== 'undefined') {
128	      this.authToken = localStorage.getItem('auth_token');
129	    }
130	  }
131	
132	  // Set authentication token
133	  setAuthToken(token: string | null): void {
134	    this.authToken = token;
135	    if (typeof window !== 'undefined') {
136	      if (token) {
137	        localStorage.setItem('auth_token', token);
138	      } else {
139	        localStorage.removeItem('auth_token');
140	      }
141	    }
142	  }
143	
144	  // Get current auth token
145	  getAuthToken(): string | null {
146	    return this.authToken;
147	  }
148	
149	  private async fetchWithTimeout(url: string, options: RequestInit = {}, retries = 3): Promise<Response> {
150	    const controller = new AbortController();
151	    const timeoutId = setTimeout(() => controller.abort(), this.timeout);
152	
153	    for (let attempt = 1; attempt <= retries; attempt++) {
154	      try {
155	        const headers: Record<string, string> = {
156	          'Content-Type': 'application/json',
157	          ...(options.headers && typeof options.headers === 'object' && !(options.headers instanceof Headers) 
158	            ? options.headers as Record<string, string> 
159	            : {}),
160	        };
161	
162	        // Skip auth for solo operator mode
163	        // if (this.authToken) {
164	        //   headers['Authorization'] = `Bearer ${this.authToken}`;
165	        // }
166	
167	        const response = await fetch(url, {
168	          ...options,
169	          signal: controller.signal,
170	          headers,
171	        });
172	        
173	        clearTimeout(timeoutId);
174	        
175	        // If response is successful or client error (4xx), don't retry
176	        if (response.ok || (response.status >= 400 && response.status < 500)) {
177	          return response;
178	        }
179	        
180	        // Server error (5xx) - retry if not last attempt
181	        if (attempt < retries) {
182	          console.warn(`Attempt ${attempt} failed with status ${response.status}, retrying...`);
183	          await new Promise(resolve => setTimeout(resolve, 1000 * attempt)); // Exponential backoff
184	          continue;
185	        }
186	        
187	        return response;
188	      } catch (error) {
189	        clearTimeout(timeoutId);
190	        
191	        if (attempt < retries && (error instanceof Error && error.name !== 'AbortError')) {
192	          console.warn(`Attempt ${attempt} failed with error: ${error.message}, retrying...`);
193	          await new Promise(resolve => setTimeout(resolve, 1000 * attempt)); // Exponential backoff
194	          continue;
195	        }
196	        
197	        throw error;
198	      }
199	    }
200	    
201	    throw new Error('All retry attempts failed');
202	  }
203	
204	  private async handleResponse<T>(response: Response): Promise<ApiResponse<T>> {
205	    try {
206	      if (!response.ok) {
207	        let errorMessage = `HTTP ${response.status}: ${response.statusText}`;
208	        
209	        // Try to get more detailed error from response body
210	        try {
211	          const errorBody = await response.text();
212	          if (errorBody) {
213	            const parsedError = JSON.parse(errorBody);
214	            errorMessage = parsedError.detail || parsedError.message || errorMessage;
215	          }
216	        } catch {
217	          // If parsing fails, use the default error message
218	        }
219	        
220	        return {
221	          error: errorMessage,
222	          status: response.status,
223	        };
224	      }
225	
226	      const data = await response.json();
227	      return {
228	        data,
229	        status: response.status,
230	      };
231	    } catch (error) {
232	      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
233	      console.error('API Response Error:', errorMessage);
234	      return {
235	        error: `Response parsing error: ${errorMessage}`,
236	        status: response.status,
237	      };
238	    }
