 13	import HyperliquidConnector from './exchange-connectors/hyperliquid-connector';
 14	import UniswapConnector from './exchange-connectors/uniswap-connector';
 15	
 16	export interface TradingManagerConfig {
 17	  exchanges: {
 18	    [name: string]: {
 19	      type: 'hyperliquid' | 'uniswap' | 'vertex' | 'bluefin' | 'bybit';
 20	      credentials: ExchangeCredentials;
 21	      enabled: boolean;
 22	      priority: number;
 23	    };
 24	  };
 25	  defaultExchange: string;
 26	  realTimeDataEnabled: boolean;
 27	  aggregateOrderBooks: boolean;
