 29	    maxPositionSize: number;
 30	    maxDailyLoss: number;
 31	    stopLossPercentage: number;
 32	    takeProfitPercentage: number;
 33	  };
 34	}
 35	
 36	export interface AggregatedMarketData extends MarketData {
 37	  exchanges: string[];
 38	  bestBid: { price: number; exchange: string };
 39	  bestAsk: { price: number; exchange: string };
 40	  totalVolume: number;
 41	}
 42	
 43	export interface AggregatedOrderBook {
 44	  symbol: string;
 45	  bids: Array<{ price: number; size: number; exchange: string }>;
 46	  asks: Array<{ price: number; size: number; exchange: string }>;
 47	  timestamp: number;
 48	  depth: number;
 49	}
 50	
 51	export interface TradingStrategy {
 52	  id: string;
 53	  name: string;
 54	  type: 'momentum' | 'mean_reversion' | 'arbitrage' | 'grid' | 'dca';
 55	  status: 'active' | 'paused' | 'stopped';
 56	  parameters: Record<string, any>;
 57	  targetSymbols: string[];
 58	  exchanges: string[];
 59	  allocation: number;
 60	  performanceMetrics: {
 61	    totalReturn: number;
 62	    sharpeRatio: number;
 63	    maxDrawdown: number;
 64	    winRate: number;
 65	    totalTrades: number;
 66	  };
 67	}
 68	
 69	export interface PortfolioSummary {
 70	  totalValue: number;
 71	  totalPnL: number;
 72	  totalPnLPercent: number;
 73	  balancesByExchange: Record<string, Balance[]>;
 74	  positionsByExchange: Record<string, Position[]>;
 75	  activeOrders: Order[];
 76	  strategies: TradingStrategy[];
 77	}
 78	
 79	export class TradingManager extends EventEmitter {
 80	  private connectors: Map<string, BaseExchangeConnector> = new Map();
 81	  private config: TradingManagerConfig;
 82	  private marketDataCache: Map<string, Map<string, MarketData>> = new Map(); // symbol -> exchange -> data
 83	  private orderBookCache: Map<string, Map<string, OrderBook>> = new Map();
 84	  private isInitialized = false;
 85	  private dataUpdateInterval: NodeJS.Timeout | null = null;
 86	  private strategies: Map<string, TradingStrategy> = new Map();
 87	
 88	  constructor(config: TradingManagerConfig) {
 89	    super();
 90	    this.config = config;
 91	    this.initializeExchanges();
 92	  }
 93	
 94	  private initializeExchanges(): void {
 95	    for (const [name, exchangeConfig] of Object.entries(this.config.exchanges)) {
 96	      if (!exchangeConfig.enabled) continue;
 97	
 98	      let connector: BaseExchangeConnector;
 99	
100	      switch (exchangeConfig.type) {
101	        case 'hyperliquid':
102	          connector = new HyperliquidConnector(exchangeConfig.credentials);
103	          break;
104	        case 'uniswap':
105	          connector = new UniswapConnector(exchangeConfig.credentials);
106	          break;
107	        case 'vertex':
108	          // TODO: Implement Vertex connector
109	          console.warn(`Vertex connector not implemented yet`);
110	          continue;
111	        case 'bluefin':
112	          // TODO: Implement Bluefin connector
113	          console.warn(`Bluefin connector not implemented yet`);
114	          continue;
115	        case 'bybit':
116	          // TODO: Implement Bybit connector
117	          console.warn(`Bybit connector not implemented yet`);
118	          continue;
119	        default:
120	          console.error(`Unknown exchange type: ${exchangeConfig.type}`);
121	          continue;
122	      }
123	
124	      // Set up event handlers
125	      connector.onMarketDataUpdate = (data: MarketData) => {
126	        this.handleMarketDataUpdate(name, data);
127	      };
128	
129	      connector.onOrderBookUpdate = (data: OrderBook) => {
130	        this.handleOrderBookUpdate(name, data);
131	      };
132	
133	      connector.onOrderUpdate = (data: Order) => {
134	        this.handleOrderUpdate(name, data);
135	      };
136	
137	      connector.onPositionUpdate = (data: Position) => {
138	        this.handlePositionUpdate(name, data);
139	      };
140	
141	      connector.onError = (error: Error) => {
142	        this.emit('exchangeError', { exchange: name, error });
143	      };
144	
145	      this.connectors.set(name, connector);
146	    }
147	  }
148	
149	  async initialize(): Promise<void> {
150	    try {
151	      // Connect to all exchanges
152	      const connectionPromises = Array.from(this.connectors.entries()).map(
153	        async ([name, connector]) => {
154	          try {
155	            const connected = await connector.connect();
156	            if (connected) {
157	              console.log(`Connected to ${name}`);
158	              this.emit('exchangeConnected', { exchange: name });
159	            } else {
160	              console.error(`Failed to connect to ${name}`);
161	              this.emit('exchangeError', { exchange: name, error: new Error('Connection failed') });
162	            }
163	          } catch (error) {
164	            console.error(`Error connecting to ${name}:`, error);
165	            this.emit('exchangeError', { exchange: name, error });
166	          }
167	        }
168	      );
169	
170	      await Promise.allSettled(connectionPromises);
171	
172	      // Start real-time data feeds if enabled
173	      if (this.config.realTimeDataEnabled) {
174	        await this.startRealTimeData();
175	      }
176	
177	      this.isInitialized = true;
178	      this.emit('initialized');
179	    } catch (error) {
180	      this.emit('error', error);
181	      throw error;
182	    }
183	  }
184	
185	  private async startRealTimeData(): Promise<void> {
186	    // Start data update interval
187	    this.dataUpdateInterval = setInterval(() => {
188	      this.updateMarketData();
189	    }, 5000); // Update every 5 seconds
190	
191	    // Connect WebSockets for real-time data
192	    for (const [name, connector] of this.connectors) {
193	      try {
194	        await connector.connectWebSocket({
195	          url: '', // Will be set by each connector
196	          subscriptions: [],
197	        });
198	
199	        // Subscribe to market data for popular symbols
200	        const popularSymbols = ['BTC', 'ETH', 'SOL', 'USDC'];
201	        await connector.subscribeToMarketData(popularSymbols);
202	        await connector.subscribeToOrderBook(popularSymbols);
203	        
204	        // Subscribe to user data if authenticated
205	        if (connector.getConnectionStatus()) {
206	          await connector.subscribeToOrders();
207	          await connector.subscribeToPositions();
208	        }
209	      } catch (error) {
210	        console.warn(`Failed to setup real-time data for ${name}:`, error);
211	      }
212	    }
213	  }
214	
215	  private async updateMarketData(): Promise<void> {
216	    const symbols = ['BTC', 'ETH', 'SOL', 'USDC', 'USDT'];
217	    
218	    for (const symbol of symbols) {
219	      const exchangeData: Record<string, MarketData> = {};
220	      
221	      for (const [name, connector] of this.connectors) {
222	        try {
223	          const data = await connector.getMarketData(symbol);
224	          exchangeData[name] = data;
225	          
226	          // Cache the data
227	          if (!this.marketDataCache.has(symbol)) {
228	            this.marketDataCache.set(symbol, new Map());
229	          }
230	          this.marketDataCache.get(symbol)!.set(name, data);
231	        } catch (error) {
232	          // Silently ignore errors for individual exchanges
233	        }
234	      }
235	
236	      // Emit aggregated market data
237	      if (Object.keys(exchangeData).length > 0) {
238	        const aggregated = this.aggregateMarketData(symbol, exchangeData);
239	        this.emit('marketDataUpdate', aggregated);
240	      }
241	    }
242	  }
243	
244	  private aggregateMarketData(symbol: string, exchangeData: Record<string, MarketData>): AggregatedMarketData {
245	    const exchanges = Object.keys(exchangeData);
246	    const prices = Object.values(exchangeData).map(d => d.price);
247	    const volumes = Object.values(exchangeData).map(d => d.volume);
248	    const bids = Object.entries(exchangeData).map(([ex, data]) => ({ price: data.bid, exchange: ex }));
249	    const asks = Object.entries(exchangeData).map(([ex, data]) => ({ price: data.ask, exchange: ex }));
250	
251	    const bestBid = bids.reduce((max, current) => current.price > max.price ? current : max);
252	    const bestAsk = asks.reduce((min, current) => current.price < min.price ? current : min);
253	
254	    const avgPrice = prices.reduce((sum, price) => sum + price, 0) / prices.length;
255	    const totalVolume = volumes.reduce((sum, vol) => sum + vol, 0);
256	
257	    return {
258	      symbol,
259	      price: avgPrice,
260	      volume: totalVolume,
261	      high24h: Math.max(...Object.values(exchangeData).map(d => d.high24h)),
262	      low24h: Math.min(...Object.values(exchangeData).map(d => d.low24h)),
263	      change24h: Object.values(exchangeData)[0]?.change24h || 0,
264	      changePercent24h: Object.values(exchangeData)[0]?.changePercent24h || 0,
265	      timestamp: Date.now(),
266	      bid: bestBid.price,
267	      ask: bestAsk.price,
268	      spread: bestAsk.price - bestBid.price,
269	      exchanges,
270	      bestBid,
271	      bestAsk,
272	      totalVolume,
273	    };
274	  }
275	
276	  private handleMarketDataUpdate(exchange: string, data: MarketData): void {
277	    // Cache the data
278	    if (!this.marketDataCache.has(data.symbol)) {
279	      this.marketDataCache.set(data.symbol, new Map());
280	    }
281	    this.marketDataCache.get(data.symbol)!.set(exchange, data);
282	
283	    // Emit individual exchange update
284	    this.emit('exchangeMarketData', { exchange, data });
285	
286	    // Create aggregated data if we have multiple exchanges
287	    const exchangeData: Record<string, MarketData> = {};
288	    const symbolCache = this.marketDataCache.get(data.symbol)!;
289	    
290	    for (const [ex, marketData] of symbolCache) {
291	      exchangeData[ex] = marketData;
292	    }
293	
294	    if (Object.keys(exchangeData).length > 1) {
295	      const aggregated = this.aggregateMarketData(data.symbol, exchangeData);
296	      this.emit('marketDataUpdate', aggregated);
297	    }
298	  }
299	
300	  private handleOrderBookUpdate(exchange: string, data: OrderBook): void {
301	    // Cache the data
302	    if (!this.orderBookCache.has(data.symbol)) {
303	      this.orderBookCache.set(data.symbol, new Map());
304	    }
305	    this.orderBookCache.get(data.symbol)!.set(exchange, data);
306	
307	    this.emit('orderBookUpdate', { exchange, data });
308	  }
309	
310	  private handleOrderUpdate(exchange: string, data: Order): void {
311	    this.emit('orderUpdate', { exchange, data });
312	  }
313	
314	  private handlePositionUpdate(exchange: string, data: Position): void {
315	    this.emit('positionUpdate', { exchange, data });
316	  }
317	
318	  // Public API methods
319	  async getExchangeInfo(exchange?: string): Promise<ExchangeInfo[]> {
320	    const results: ExchangeInfo[] = [];
321	    const targets = exchange ? [exchange] : Array.from(this.connectors.keys());
322	
323	    for (const name of targets) {
324	      const connector = this.connectors.get(name);
325	      if (connector) {
326	        try {
327	          const info = await connector.getExchangeInfo();
328	          results.push(info);
329	        } catch (error) {
330	          console.error(`Failed to get exchange info for ${name}:`, error);
331	        }
332	      }
333	    }
334	
335	    return results;
336	  }
337	
338	  async getMarketData(symbol: string, exchange?: string): Promise<MarketData | AggregatedMarketData> {
339	    if (exchange) {
340	      const connector = this.connectors.get(exchange);
341	      if (!connector) {
342	        throw new Error(`Exchange ${exchange} not found`);
343	      }
344	      return await connector.getMarketData(symbol);
345	    }
346	
347	    // Return aggregated data
348	    const exchangeData: Record<string, MarketData> = {};
349	    
350	    for (const [name, connector] of this.connectors) {
351	      try {
352	        const data = await connector.getMarketData(symbol);
353	        exchangeData[name] = data;
354	      } catch (error) {
355	        // Continue with other exchanges
356	      }
357	    }
358	
359	    if (Object.keys(exchangeData).length === 0) {
360	      throw new Error(`No market data available for ${symbol}`);
361	    }
362	
363	    return this.aggregateMarketData(symbol, exchangeData);
364	  }
365	
366	  async getPortfolioSummary(): Promise<PortfolioSummary> {
367	    const balancesByExchange: Record<string, Balance[]> = {};
368	    const positionsByExchange: Record<string, Position[]> = {};
369	    const allOrders: Order[] = [];
370	
371	    // Collect data from all exchanges
372	    for (const [name, connector] of this.connectors) {
373	      try {
374	        const [balances, positions, orders] = await Promise.all([
375	          connector.getBalances(),
376	          connector.getPositions(),
377	          connector.getOrders(),
378	        ]);
379	
380	        balancesByExchange[name] = balances;
381	        positionsByExchange[name] = positions;
382	        allOrders.push(...orders.map(order => ({ ...order, exchange: name } as any)));
383	      } catch (error) {
384	        console.error(`Failed to get portfolio data from ${name}:`, error);
385	      }
386	    }
387	
388	    // Calculate total portfolio value
389	    let totalValue = 0;
390	    let totalPnL = 0;
391	
392	    for (const balances of Object.values(balancesByExchange)) {
393	      for (const balance of balances) {
394	        totalValue += balance.usdValue || 0;
395	      }
396	    }
397	
398	    for (const positions of Object.values(positionsByExchange)) {
399	      for (const position of positions) {
400	        totalPnL += position.unrealizedPnl + position.realizedPnl;
401	      }
402	    }
403	
404	    const totalPnLPercent = totalValue > 0 ? (totalPnL / totalValue) * 100 : 0;
405	
406	    return {
407	      totalValue,
408	      totalPnL,
409	      totalPnLPercent,
410	      balancesByExchange,
411	      positionsByExchange,
412	      activeOrders: allOrders.filter(order => order.status === 'open' || order.status === 'pending'),
413	      strategies: Array.from(this.strategies.values()),
414	    };
