/* Dark Mode Trading Dashboard Design System v2.0 */

/* Import Inter font */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

/* Modern ShadCN Design System Variables */
:root {
  /* Primary Brand Colors */
  --color-primary-blue: #3B82F6;
  --color-primary-blue-light: #60A5FA;
  --color-primary-blue-dark: #1D4ED8;
  --color-primary-blue-bg: rgba(59, 130, 246, 0.1);

  --color-primary-green: #10B981;
  --color-primary-green-light: #34D399;
  --color-primary-green-dark: #059669;
  --color-primary-green-bg: rgba(16, 185, 129, 0.1);

  --color-primary-purple: #8B5CF6;
  --color-primary-purple-light: #A78BFA;
  --color-primary-purple-dark: #7C3AED;
  --color-primary-purple-bg: rgba(139, 92, 246, 0.1);

  /* Semantic Colors */
  --color-success: #10B981;
  --color-warning: #F59E0B;
  --color-error: #EF4444;
  --color-info: #3B82F6;

  /* ShadCN CSS Variables - Modern oklch Color System */
  --background: oklch(0.09 0 0);
  --foreground: oklch(0.98 0 0);
  --card: oklch(0.12 0 0);
  --card-foreground: oklch(0.98 0 0);
  --popover: oklch(0.12 0 0);
  --popover-foreground: oklch(0.98 0 0);
  --primary: oklch(0.62 0.25 264);
  --primary-foreground: oklch(0.98 0 0);
  --secondary: oklch(0.16 0 0);
  --secondary-foreground: oklch(0.98 0 0);
  --muted: oklch(0.16 0 0);
  --muted-foreground: oklch(0.65 0 0);
  --accent: oklch(0.16 0 0);
  --accent-foreground: oklch(0.98 0 0);
  --destructive: oklch(0.65 0.25 29);
  --destructive-foreground: oklch(0.98 0 0);
  --border: oklch(0.22 0 0);
  --input: oklch(0.16 0 0);
  --ring: oklch(0.62 0.25 264);
  --radius: 0.5rem;
  --chart-1: oklch(0.62 0.25 264);
  --chart-2: oklch(0.70 0.25 156);
  --chart-3: oklch(0.65 0.25 295);
  --chart-4: oklch(0.75 0.25 67);
  --chart-5: oklch(0.65 0.25 29);

  /* Dark Theme Backgrounds & Surfaces - ShadCN Aligned */
  --color-bg-primary: oklch(0.09 0 0);
  --color-bg-secondary: oklch(0.11 0 0);
  --color-bg-tertiary: oklch(0.16 0 0);
  --color-bg-elevated: oklch(0.18 0 0);
  --color-bg-card: oklch(0.12 0 0);
  --color-bg-overlay: oklch(0.05 0 0 / 0.8);

  /* Dark Theme Text Colors - ShadCN Aligned */
  --color-text-primary: oklch(0.98 0 0);
  --color-text-secondary: oklch(0.85 0 0);
  --color-text-tertiary: oklch(0.65 0 0);
  --color-text-muted: oklch(0.45 0 0);
  --color-text-disabled: oklch(0.35 0 0);

  /* Dark Theme Borders - ShadCN Aligned */
  --color-border-primary: oklch(0.22 0 0);
  --color-border-secondary: oklch(0.28 0 0);
  --color-border-accent: oklch(0.35 0 0);

  /* Typography */
  --font-primary: 'Inter', 'SF Pro Display', -apple-system, BlinkMacSystemFont, sans-serif;
  --font-secondary: 'Inter', 'SF Pro Text', -apple-system, BlinkMacSystemFont, sans-serif;
  --font-mono: 'SF Mono', Monaco, 'Cascadia Code', monospace;

  /* Font Sizes */
  --text-xs: 0.75rem;    /* 12px */
  --text-sm: 0.875rem;   /* 14px */
  --text-base: 1rem;     /* 16px */
  --text-lg: 1.125rem;   /* 18px */
  --text-xl: 1.25rem;    /* 20px */
  --text-2xl: 1.5rem;    /* 24px */
  --text-3xl: 1.875rem;  /* 30px */
  --text-4xl: 2.25rem;   /* 36px */
  --text-5xl: 3rem;      /* 48px */

  /* Font Weights */
  --font-light: 300;
  --font-regular: 400;
  --font-medium: 500;
  --font-semibold: 600;
  --font-bold: 700;

  /* Line Heights */
  --leading-tight: 1.25;
  --leading-normal: 1.5;
  --leading-relaxed: 1.75;

  /* Spacing Scale */
  --space-1: 0.25rem;    /* 4px */
  --space-2: 0.5rem;     /* 8px */
  --space-3: 0.75rem;    /* 12px */
  --space-4: 1rem;       /* 16px */
  --space-5: 1.25rem;    /* 20px */
  --space-6: 1.5rem;     /* 24px */
  --space-8: 2rem;       /* 32px */
  --space-10: 2.5rem;    /* 40px */
  --space-12: 3rem;      /* 48px */
  --space-16: 4rem;      /* 64px */

  /* Border Radius */
  --radius-sm: 0.375rem; /* 6px */
  --radius-md: 0.5rem;   /* 8px */
  --radius-lg: 0.75rem;  /* 12px */
  --radius-xl: 1rem;     /* 16px */
  --radius-2xl: 1.5rem;  /* 24px */
  --radius-full: 9999px;

  /* Shadows */
  --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.5);
  --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.4), 0 2px 4px -1px rgba(0, 0, 0, 0.3);
  --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.4), 0 4px 6px -2px rgba(0, 0, 0, 0.3);
  --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.4), 0 10px 10px -5px rgba(0, 0, 0, 0.3);
  --shadow-inner: inset 0 2px 4px 0 rgba(0, 0, 0, 0.3);

  /* Transitions */
  --transition-fast: 150ms cubic-bezier(0.4, 0, 0.2, 1);
  --transition-normal: 200ms cubic-bezier(0.4, 0, 0.2, 1);
  --transition-slow: 300ms cubic-bezier(0.4, 0, 0.2, 1);
}

/* Global Dark Theme Styles */
* {
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
  height: 100%;
}

body {
  font-family: var(--font-primary);
  font-size: var(--text-base);
  line-height: var(--leading-normal);
  color: hsl(var(--foreground));
  background-color: hsl(var(--background)) !important;
  margin: 0;
  padding: 0;
  height: 100%;
  width: 100%;
  overflow-x: hidden;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

#root, #__next {
  height: 100%;
  width: 100%;
  background-color: hsl(var(--background)) !important;
}

/* Eliminate ALL white backgrounds */
* {
  box-sizing: border-box;
}

html {
  background-color: hsl(var(--background)) !important;
  width: 100%;
}

/* Override any white/light backgrounds */
.bg-white,
.bg-gray-50,
.bg-gray-100,
.bg-slate-50,
.bg-slate-100,
.bg-neutral-50,
.bg-neutral-100,
.bg-stone-50,
.bg-stone-100,
.bg-red-50,
.bg-orange-50,
.bg-amber-50,
.bg-yellow-50,
.bg-lime-50,
.bg-green-50,
.bg-emerald-50,
.bg-teal-50,
.bg-cyan-50,
.bg-sky-50,
.bg-blue-50,
.bg-indigo-50,
.bg-violet-50,
.bg-purple-50,
.bg-fuchsia-50,
.bg-pink-50,
.bg-rose-50 {
  background-color: hsl(var(--card)) !important;
}

/* Ensure all containers inherit dark background */
div,
section,
article,
aside,
header,
footer,
main,
nav {
  background-color: inherit;
}

/* Dark Dashboard Container */
.dashboard-dark {
  min-height: 100vh;
  width: 100vw;
  background-color: hsl(var(--background)) !important;
  color: hsl(var(--foreground));
  font-family: var(--font-primary);
  margin: 0;
  padding: 0;
}

/* Full Width Dashboard Container */
.dashboard-container {
  display: flex;
  min-height: 100vh;
  width: 100vw;
  background-color: var(--color-bg-primary) !important;
  margin: 0;
  padding: 0;

  /* Mobile first */
  flex-direction: column;
}

@media (min-width: 1024px) {
  .dashboard-container {
    flex-direction: row;
  }
}

/* Sidebar Styles */
.sidebar-dark {
  background-color: var(--color-bg-card);
  border-right: 1px solid var(--color-border-primary);
  color: var(--color-text-primary);
  width: 100%;
  height: auto;
  overflow-y: auto;

  /* Mobile */
  position: fixed;
  top: 0;
  left: 0;
  z-index: 50;
  transform: translateX(-100%);
  transition: transform var(--transition-normal);
}

.sidebar-dark.open {
  transform: translateX(0);
}

@media (min-width: 1024px) {
  .sidebar-dark {
    position: static;
    width: 280px;
    height: 100vh;
    transform: translateX(0);
    flex-shrink: 0;
  }
}

.sidebar-header {
  padding: var(--space-6);
  border-bottom: 1px solid var(--color-border-primary);
}

.sidebar-nav {
  padding: var(--space-4);
  display: flex;
  flex-direction: column;
  gap: var(--space-2);
}

.sidebar-link {
  display: flex;
  align-items: center;
  gap: var(--space-3);
  padding: var(--space-3) var(--space-4);
  border-radius: var(--radius-lg);
  color: var(--color-text-secondary);
  text-decoration: none;
  font-weight: var(--font-medium);
  font-size: var(--text-sm);
  transition: all var(--transition-normal);
  border: none;
  background: none;
  cursor: pointer;
  width: 100%;
  text-align: left;
}

.sidebar-link:hover {
  background-color: var(--color-bg-elevated);
  color: var(--color-text-primary);
  transform: translateX(4px);
}

.sidebar-link.active {
  background-color: var(--color-primary-blue-bg);
  color: var(--color-primary-blue);
  border-left: 3px solid var(--color-primary-blue);
}

/* Main Content Area */
.main-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  width: 100%;
  background-color: var(--color-bg-secondary) !important;
  overflow-x: hidden;
  margin: 0;
  padding: 0;
}

@media (min-width: 1024px) {
  .main-content {
    width: calc(100% - 280px);
  }
}

/* Header */
.dashboard-header {
  background-color: var(--color-bg-card);
  border-bottom: 1px solid var(--color-border-primary);
  padding: var(--space-4) var(--space-6);
  box-shadow: var(--shadow-sm);
  position: sticky;
  top: 0;
  z-index: 40;
}

/* Content Area - Full Width Fixed Layout */
.dashboard-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  background-color: var(--color-bg-secondary) !important;
  height: calc(100vh - 80px);
  width: 100%;
  overflow: hidden;
  margin: 0;
  padding: 0;
}

/* Modern Card System - Dark Background */
.card-dark,
.modern-card,
[data-radix-card],
.card {
  background-color: var(--color-bg-card) !important;
  border: 1px solid var(--color-border-primary);
  border-radius: var(--radius-xl);
  padding: var(--space-6);
  box-shadow: var(--shadow-md);
  transition: all var(--transition-normal);
  overflow: hidden;
  color: var(--color-text-primary) !important;
}

.card-dark:hover,
.modern-card:hover,
[data-radix-card]:hover,
.card:hover {
  box-shadow: var(--shadow-lg);
  border-color: var(--color-border-secondary);
  transform: translateY(-2px);
}

/* Metric Cards */
.metric-card-dark {
  background-color: var(--color-bg-card);
  border: 1px solid var(--color-border-primary);
  border-radius: var(--radius-xl);
  padding: var(--space-6);
  box-shadow: var(--shadow-md);
  transition: all var(--transition-normal);
  position: relative;
  overflow: hidden;
}

.metric-card-dark::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, var(--color-primary-blue), var(--color-primary-green));
}

.metric-title-dark {
  font-size: var(--text-sm);
  color: var(--color-text-tertiary);
  font-weight: var(--font-medium);
  margin-bottom: var(--space-2);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.metric-value-dark {
  font-size: var(--text-3xl);
  font-weight: var(--font-bold);
  color: var(--color-text-primary);
  margin-bottom: var(--space-2);
  line-height: var(--leading-tight);
}

.metric-change-dark {
  font-size: var(--text-sm);
  font-weight: var(--font-medium);
  display: flex;
  align-items: center;
  gap: var(--space-1);
}

.metric-change-dark.positive {
  color: var(--color-success);
}

.metric-change-dark.negative {
  color: var(--color-error);
}

.metric-change-dark.neutral {
  color: var(--color-text-tertiary);
}

/* Fixed Navigation Tabs */
.nav-tabs-dark {
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border-primary);
  border-radius: var(--radius-xl);
  padding: var(--space-2);
  margin: var(--space-4) var(--space-6) 0 var(--space-6);
  display: flex;
  gap: var(--space-1);
  flex-shrink: 0;

  /* Mobile: horizontal scroll */
  overflow-x: auto;
  scrollbar-width: none;
  -ms-overflow-style: none;
  flex-wrap: nowrap;
}

.nav-tabs-dark::-webkit-scrollbar {
  display: none;
}

/* Responsive navigation adjustments */
@media (max-width: 768px) {
  .nav-tabs-dark {
    margin: var(--space-2) var(--space-4) 0 var(--space-4);
  }
}

@media (min-width: 768px) {
  .nav-tabs-dark {
    flex-wrap: wrap;
    overflow-x: visible;
    justify-content: center;
  }
}

@media (min-width: 1024px) {
  .nav-tabs-dark {
    justify-content: space-between;
  }
}

.nav-tab-dark {
  padding: var(--space-2) var(--space-3);
  border-radius: var(--radius-lg);
  color: var(--color-text-tertiary);
  font-weight: var(--font-medium);
  font-size: var(--text-xs);
  text-decoration: none;
  transition: all var(--transition-normal);
  display: flex;
  align-items: center;
  gap: var(--space-1);
  border: none;
  background: none;
  cursor: pointer;
  white-space: nowrap;
  flex-shrink: 0;
  min-width: fit-content;
}

/* Responsive tab sizing */
@media (min-width: 640px) {
  .nav-tab-dark {
    padding: var(--space-3) var(--space-4);
    font-size: var(--text-sm);
    gap: var(--space-2);
  }
}

@media (min-width: 1024px) {
  .nav-tab-dark {
    flex: 1;
    justify-content: center;
    min-width: 80px;
  }
}

.nav-tab-dark:hover:not(.active) {
  color: var(--color-text-secondary);
  background-color: var(--color-bg-tertiary);
}

.nav-tab-dark.active {
  background-color: var(--color-primary-blue);
  color: white;
  box-shadow: var(--shadow-sm);
}

/* Button System */
.btn-dark {
  font-family: var(--font-primary);
  font-weight: var(--font-medium);
  font-size: var(--text-sm);
  border-radius: var(--radius-lg);
  padding: var(--space-3) var(--space-4);
  transition: all var(--transition-normal);
  border: none;
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
}

.btn-primary-dark {
  background-color: var(--color-primary-blue);
  color: white;
}

.btn-primary-dark:hover {
  background-color: var(--color-primary-blue-dark);
  transform: translateY(-1px);
  box-shadow: var(--shadow-md);
}

.btn-secondary-dark {
  background-color: transparent;
  color: var(--color-text-secondary);
  border: 1px solid var(--color-border-primary);
}

.btn-secondary-dark:hover {
  background-color: var(--color-bg-elevated);
  color: var(--color-text-primary);
  border-color: var(--color-border-secondary);
}

.btn-ghost-dark {
  background-color: transparent;
  color: var(--color-text-secondary);
  border: none;
}

.btn-ghost-dark:hover {
  background-color: var(--color-bg-elevated);
  color: var(--color-text-primary);
}

/* Form Elements */
input, textarea, select {
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border-primary);
  border-radius: var(--radius-lg);
  padding: var(--space-3) var(--space-4);
  font-family: var(--font-secondary);
  font-size: var(--text-base);
  color: var(--color-text-primary);
  transition: all var(--transition-normal);
  width: 100%;
}

input:focus, textarea:focus, select:focus {
  outline: none;
  border-color: var(--color-primary-blue);
  box-shadow: 0 0 0 3px var(--color-primary-blue-bg);
}

input::placeholder, textarea::placeholder {
  color: var(--color-text-muted);
}

/* Badges */
.badge-dark {
  display: inline-flex;
  align-items: center;
  padding: var(--space-1) var(--space-3);
  border-radius: var(--radius-full);
  font-size: var(--text-xs);
  font-weight: var(--font-medium);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.badge-success-dark {
  background-color: var(--color-primary-green-bg);
  color: var(--color-success);
  border: 1px solid var(--color-success);
}

.badge-warning-dark {
  background-color: rgba(245, 158, 11, 0.1);
  color: var(--color-warning);
  border: 1px solid var(--color-warning);
}

.badge-error-dark {
  background-color: rgba(239, 68, 68, 0.1);
  color: var(--color-error);
  border: 1px solid var(--color-error);
}

.badge-info-dark {
  background-color: var(--color-primary-blue-bg);
  color: var(--color-info);
  border: 1px solid var(--color-info);
}

/* Chart Container */
.chart-container-dark {
  background-color: var(--color-bg-card);
  border: 1px solid var(--color-border-primary);
  border-radius: var(--radius-xl);
  padding: var(--space-6);
  box-shadow: var(--shadow-md);
  overflow: hidden;
}

/* Grid System */
.grid-responsive {
  display: grid;
  gap: var(--space-6);

  /* Mobile: 1 column */
  grid-template-columns: 1fr;
}

@media (min-width: 640px) {
  .grid-responsive {
    /* Tablet: 2 columns */
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .grid-responsive {
    /* Desktop: 3 columns */
    grid-template-columns: repeat(3, 1fr);
  }
}

@media (min-width: 1280px) {
  .grid-responsive {
    /* Large: 4 columns */
    grid-template-columns: repeat(4, 1fr);
  }
}

/* Mobile Overlay */
.mobile-overlay {
  position: fixed;
  inset: 0;
  background-color: var(--color-bg-overlay);
  z-index: 40;
  display: block;
}

@media (min-width: 1024px) {
  .mobile-overlay {
    display: none;
  }
}

/* Loading States */
.loading-spinner {
  width: 24px;
  height: 24px;
  border: 2px solid var(--color-border-primary);
  border-top: 2px solid var(--color-primary-blue);
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Animations */
.animate-fade-in {
  animation: fadeIn var(--transition-normal) ease-in-out;
}

.animate-slide-up {
  animation: slideUp var(--transition-slow) ease-out;
}

.animate-scale-in {
  animation: scaleIn var(--transition-normal) ease-out;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from { 
    opacity: 0;
    transform: translateY(20px);
  }
  to { 
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleIn {
  from { 
    opacity: 0;
    transform: scale(0.95);
  }
  to { 
    opacity: 1;
    transform: scale(1);
  }
}

/* Scrollbar Styling */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--color-bg-secondary);
}

::-webkit-scrollbar-thumb {
  background: var(--color-border-secondary);
  border-radius: var(--radius-full);
}

::-webkit-scrollbar-thumb:hover {
  background: var(--color-border-accent);
}

/* Focus States for Accessibility */
.focus-visible {
  outline: 2px solid var(--color-primary-blue);
  outline-offset: 2px;
}

/* Dynamic Content Filling */
.dashboard-content > div:last-child {
  flex: 1;
  display: flex;
  flex-direction: column;
  min-height: 0;
}

/* Ensure tab content fills available space */
.dashboard-content .space-y-6,
.dashboard-content .space-y-4 {
  height: 100%;
  display: flex;
  flex-direction: column;
  gap: var(--space-6);
}

/* Make grids and cards responsive to container height */
.dashboard-content .grid {
  flex: 1;
  align-content: start;
}

/* Mobile Menu Button */
.mobile-menu-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border: none;
  background: none;
  color: var(--color-text-secondary);
  cursor: pointer;
  border-radius: var(--radius-lg);
  transition: all var(--transition-normal);
}

.mobile-menu-btn:hover {
  background-color: var(--color-bg-elevated);
  color: var(--color-text-primary);
}

@media (min-width: 1024px) {
  .mobile-menu-btn {
    display: none;
  }
}

/* Utility Classes */
.text-primary { color: var(--color-text-primary); }
.text-secondary { color: var(--color-text-secondary); }
.text-tertiary { color: var(--color-text-tertiary); }
.text-muted { color: var(--color-text-muted); }

.bg-primary { background-color: var(--color-bg-primary); }
.bg-secondary { background-color: var(--color-bg-secondary); }
.bg-card { background-color: var(--color-bg-card); }
.bg-elevated { background-color: var(--color-bg-elevated); }

.border-primary { border-color: var(--color-border-primary); }
.border-secondary { border-color: var(--color-border-secondary); }

/* High contrast mode */
@media (prefers-contrast: high) {
  :root {
    --color-text-primary: #FFFFFF;
    --color-text-secondary: #E5E5E5;
    --color-border-primary: #666666;
    --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.6), 0 2px 4px -1px rgba(0, 0, 0, 0.4);
  }
}

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

