interface DashboardMetrics {
  totalValue: number
  dailyPnL: number
  totalPnL: number
  activePositions: number
  activeAgents: number
  activeFarms: number
  winRate: number
  avgReturn: number
  riskScore: number
}

interface ChartDataPoint {
  time: string
  value: number
  pnl: number
}

interface DashboardTab {
  id: string
  label: string
  icon: React.ReactNode
  component: React.ReactNode
}

// Mock chart data for portfolio performance
const generateChartData = (): ChartDataPoint[] => {
  const data: ChartDataPoint[] = []
  const now = new Date()

  for (let i = 29; i >= 0; i--) {
    const date = new Date(now.getTime() - i * 24 * 60 * 60 * 1000)
    const baseValue = 125000
    const variation = Math.sin(i * 0.1) * 5000 + Math.random() * 2000

    data.push({
      time: date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
      value: baseValue + variation,
      pnl: variation * 0.1
    })
  }

  return data
}

export function ModernDashboardV4() {
  const [activeTab, setActiveTab] = useState('overview')
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false)
  const [isLoading, setIsLoading] = useState(true)
  const [chartData] = useState<ChartDataPoint[]>(generateChartData())

  // Remove unused backendClient reference
  // const backendClient = backendApi

  // Dashboard metrics state with v4 minimal mock data
  const [metrics, setMetrics] = useState<DashboardMetrics>({
    totalValue: 125847.32,
    dailyPnL: 2847.32,
    totalPnL: 25847.32,
    activePositions: 8,
    activeAgents: 5, // Our 5 expert agents
    activeFarms: 3,
    winRate: 78.4,
    avgReturn: 15.2,
    riskScore: 23.5
  })

  // Quick loading - no external dependencies
  useEffect(() => {
    // Immediately set loading to false - no backend dependencies
    setIsLoading(false)
  }, [])

  // Flat navigation - all tabs in one row as requested
  const tabs: DashboardTab[] = [
    {
      id: 'overview',
      label: 'Overview',
      icon: <BarChart3 className="h-4 w-4" />,
      component: <OverviewTab metrics={metrics} chartData={chartData} />
    },
    {
      id: 'agents',
      label: 'Agents',
      icon: <Bot className="h-4 w-4" />,
      component: <AgentsTab />
    },
    {
      id: 'farms',
      label: 'Farms',
      icon: <Target className="h-4 w-4" />,
      component: <FarmsTab />
    },
    {
      id: 'goals',
      label: 'Goals',
      icon: <Star className="h-4 w-4" />,
      component: <GoalsTab />
    },
    {
      id: 'eliza',
      label: 'Eliza AI',
      icon: <Bot className="h-4 w-4" />,
      component: <ElizaPage />
    },
    {
      id: 'trading',
      label: 'Trading',
      icon: <TrendingUp className="h-4 w-4" />,
      component: <TradingTab />
    },
    {
      id: 'vault',
      label: 'Vault',
      icon: <Wallet className="h-4 w-4" />,
      component: <VaultTab />
    },
    {
      id: 'calendar',
      label: 'Calendar',
      icon: <Calendar className="h-4 w-4" />,
      component: <CalendarPage />
    },
    {
      id: 'advanced',
      label: 'Advanced',
      icon: <Settings className="h-4 w-4" />,
      component: <AdvancedTab />
    }
  ]

  if (isLoading) {
    return <LoadingScreen />
  }

  return (
    <div className="dashboard-dark">
      {/* Mobile Menu Overlay */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="mobile-overlay lg:hidden"
            onClick={() => setIsMobileMenuOpen(false)}
          />
        )}
      </AnimatePresence>

      <div className="dashboard-container">
        {/* Sidebar */}
        <aside className={`sidebar-dark ${isMobileMenuOpen ? 'open' : ''}`}>
          <div className="sidebar-header">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-xl font-bold text-primary">
                  Cival Dashboard
                </h2>
                <p className="text-sm text-tertiary">AI Trading Platform</p>
              </div>
              <button
                className="mobile-menu-btn lg:hidden"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                <X className="h-5 w-5" />
              </button>
            </div>
          </div>

          <nav className="sidebar-nav">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => {
                  setActiveTab(tab.id)
                  setIsMobileMenuOpen(false)
                }}
                className={`sidebar-link ${activeTab === tab.id ? 'active' : ''}`}
              >
                {tab.icon}
                {tab.label}
              </button>
            ))}
          </nav>

          <div className="mt-auto p-4 border-t border-primary">
            <div className="card-dark p-3">
              <div className="flex items-center gap-2 text-success">
                <div className="w-2 h-2 rounded-full bg-success animate-pulse"></div>
                <span className="text-sm font-medium">System Online</span>
              </div>
              <p className="text-xs text-muted mt-1">All systems operational</p>
            </div>
          </div>
        </aside>

        {/* Main Content */}
        <main className="main-content">
          {/* Header */}
          <header className="dashboard-header">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <button
                  className="mobile-menu-btn"
                  onClick={() => setIsMobileMenuOpen(true)}
                >
                  <Menu className="h-5 w-5" />
                </button>
                <div>
                  <h1 className="text-2xl font-bold text-primary">
                    Cival Dashboard
                  </h1>
                  <p className="text-sm text-secondary">Advanced AI Trading Platform</p>
                </div>
              </div>

              <div className="flex items-center gap-3">
                <div className="badge-success-dark">
                  Live
                </div>
                <button className="btn-secondary-dark hidden sm:flex">
                  <Bell className="h-4 w-4 mr-2" />
                  Alerts
                </button>
                <button className="btn-ghost-dark">
                  <Settings className="h-4 w-4" />
                </button>
              </div>
            </div>
          </header>

          {/* Dashboard Content */}
          <div className="dashboard-content">
            {/* Fixed Navigation Tabs */}
            <div className="nav-tabs-dark">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`nav-tab-dark ${activeTab === tab.id ? 'active' : ''}`}
                >
                  {tab.icon}
                  <span className="hidden xs:inline">{tab.label}</span>
                </button>
              ))}
            </div>

            {/* Dynamic Content Area */}
            <div className="flex-1 overflow-y-auto p-6">
              {tabs.find(tab => tab.id === activeTab)?.component}
            </div>
          </div>
        </main>
      </div>
    </div>
  )
}

// Enhanced Overview Tab with Real Trading Functionality
function OverviewTab({ metrics, chartData }: { metrics: DashboardMetrics; chartData: ChartDataPoint[] }) {
  const [overviewTab, setOverviewTab] = useState('dashboard')

  const overviewSubTabs = [
    { id: 'dashboard', label: 'Dashboard', component: <DashboardOverview metrics={metrics} chartData={chartData} /> },
    { id: 'portfolio', label: 'Portfolio', component: <PortfolioMonitor /> },
    { id: 'trading', label: 'Trading', component: <TradingInterface /> },
    { id: 'agents', label: 'Agent Manager', component: <AgentManager /> },
    { id: 'risk', label: 'Risk Monitor', component: <RiskDashboard /> }
  ]

  return (
    <div className="space-y-6">
      {/* Live Market Ticker */}
      <Card className="modern-card">
        <CardContent className="p-4">
          <LiveMarketTicker />
        </CardContent>
      </Card>

      {/* Overview Sub-Navigation */}
      <Tabs value={overviewTab} onValueChange={setOverviewTab} className="space-y-4">
        <TabsList className="grid w-full grid-cols-2 md:grid-cols-5 bg-emerald-50 gap-2">
          {overviewSubTabs.map((tab) => (
            <TabsTrigger
              key={tab.id}
              value={tab.id}
              className="data-[state=active]:bg-emerald-100 data-[state=active]:text-emerald-900 text-xs sm:text-sm p-2 truncate"
            >
              {tab.label}
            </TabsTrigger>
          ))}
        </TabsList>

        {overviewSubTabs.map((tab) => (
          <TabsContent key={tab.id} value={tab.id}>
            {tab.component}
          </TabsContent>
        ))}
      </Tabs>
    </div>
  )
}

// Modern Metric Card Component
function ModernMetricCard({ 
  title, 
  value, 
  change, 
  changePercent, 
  isPositive, 
  icon 
}: { 
  title: string; 
  value: string; 
  change: string; 
  changePercent?: string; 
  isPositive: boolean; 
  icon: React.ReactNode; 
}) {
  return (
    <div className="metric-card-dark">
      <div className="flex items-start justify-between mb-3">
        <div className="metric-title-dark">{title}</div>
        <div className="text-primary">
          {icon}
        </div>
      </div>
      <div className="metric-value-dark">{value}</div>
      <div className={`metric-change-dark ${isPositive ? 'positive' : 'negative'}`}>
        {change} {changePercent && <span className="ml-1">{changePercent}</span>}
      </div>
    </div>
  )
}

// Dashboard Overview Component (existing functionality)
function DashboardOverview({ metrics, chartData }: { metrics: DashboardMetrics; chartData: ChartDataPoint[] }) {
  return (
    <div className="space-y-6">
      {/* Key Metrics Cards */}
      <div className="grid-responsive">
        <ModernMetricCard
          title="Total Portfolio"
          value={`$${metrics.totalValue.toLocaleString('en-US', { minimumFractionDigits: 2 })}`}
          change={`+${metrics.dailyPnL.toFixed(2)}`}
          changePercent="+2.34%"
          isPositive={metrics.dailyPnL > 0}
          icon={<DollarSign className="h-5 w-5" />}
        />

        <ModernMetricCard
          title="Active Agents"
          value={metrics.activeAgents.toString()}
          change="Expert Agents"
          isPositive={true}
          icon={<Bot className="h-5 w-5" />}
        />

        <ModernMetricCard
          title="Win Rate"
          value={`${metrics.winRate}%`}
          change="Last 30 days"
          isPositive={metrics.winRate > 70}
          icon={<Target className="h-5 w-5" />}
        />

        <ModernMetricCard
          title="Risk Score"
          value={`${metrics.riskScore}%`}
          change="Low Risk"
          isPositive={metrics.riskScore < 30}
          icon={<Shield className="h-5 w-5" />}
        />
      </div>

      {/* Performance Chart */}
      <div className="chart-container-dark">
        <div className="flex items-center gap-2 mb-4">
          <div className="text-success">
            <BarChart3 className="h-5 w-5" />
          </div>
          <h3 className="text-lg font-semibold text-primary">
            Portfolio Performance
          </h3>
        </div>
        <p className="text-sm mb-4 text-secondary">30-day portfolio value and P&L trend</p>

        <div className="h-80">
          <ResponsiveContainer width="100%" height="100%">
            <AreaChart data={chartData}>
              <defs>
                <linearGradient id="portfolioGradient" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#10B981" stopOpacity={0.3}/>
                  <stop offset="95%" stopColor="#10B981" stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
              <XAxis dataKey="time" stroke="#9CA3AF" />
              <YAxis stroke="#9CA3AF" />
              <Tooltip 
                contentStyle={{
                  backgroundColor: '#1F1F1F',
                  border: '1px solid #374151',
                  borderRadius: '12px',
                  boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.4)',
                  color: '#F9FAFB'
                }}
              />
              <Area
                type="monotone"
                dataKey="value"
                stroke="#10B981"
                strokeWidth={2}
                fill="url(#portfolioGradient)"
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Quick Actions */}
      <Card className="bg-white/80 backdrop-blur-sm border-emerald-200/50">
        <CardHeader>
          <CardTitle>Quick Actions</CardTitle>
          <CardDescription>Frequently used trading operations</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid gap-3 grid-cols-1 sm:grid-cols-3">
            <Button className="bg-gradient-to-r from-emerald-500 to-emerald-600 hover:from-emerald-600 hover:to-emerald-700 text-white w-full">
              <Plus className="h-4 w-4 mr-2" />
              <span className="truncate">New Strategy</span>
            </Button>
            <Button variant="outline" className="border-violet-200 text-violet-700 hover:bg-violet-50 w-full">
              <Bot className="h-4 w-4 mr-2" />
              <span className="truncate">Deploy Agent</span>
            </Button>
            <Button variant="outline" className="border-amber-200 text-amber-700 hover:bg-amber-50 w-full">
              <Target className="h-4 w-4 mr-2" />
