            )}
          </div>

          <div className="flex items-center gap-2">
            <Badge variant="outline">
              {images.length}/{maxFiles}
            </Badge>
            {uploading && (
              <Badge variant="secondary" className="animate-pulse">
                Uploading...
              </Badge>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Drop Zone */}
        <div
          ref={dropZoneRef}
          className={cn(
            "border-2 border-dashed rounded-lg p-8 text-center cursor-pointer transition-colors",
            dragOver ? "border-primary bg-primary/5" : "border-muted-foreground/25",
            "hover:border-primary hover:bg-primary/5"
          )}
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onClick={() => fileInputRef.current?.click()}
        >
          <div className="space-y-4">
            <div className="mx-auto w-12 h-12 bg-muted rounded-full flex items-center justify-center">
              {dragOver ? (
                <Download className="h-6 w-6 text-primary" />
              ) : (
                <Upload className="h-6 w-6 text-muted-foreground" />
              )}
            </div>

            {children || (
              <div className="space-y-2">
                <p className="text-lg font-medium">
                  {dragOver ? 'Drop images here' : 'Upload images'}
                </p>
                <p className="text-sm text-muted-foreground">
                  Drag & drop or click to browse
                </p>
                <p className="text-xs text-muted-foreground">
                  Max {maxFiles} files, {formatFileSize(maxSize)} each
                </p>
              </div>
            )}

            <div className="flex items-center justify-center gap-4">
              <Button variant="outline" size="sm">
                <Folder className="h-4 w-4 mr-2" />
                Browse Files
              </Button>
              {navigator.mediaDevices && (
                <Button variant="outline" size="sm">
                  <Camera className="h-4 w-4 mr-2" />
                  Take Photo
                </Button>
              )}
            </div>
          </div>

          <input
            ref={fileInputRef}
            type="file"
            accept={accept}
            multiple={multiple}
            className="hidden"
            onChange={(e) => e.target.files && handleFileSelect(e.target.files)}
          />
        </div>

        {/* Uploaded Images Grid */}
        {images.length > 0 && (
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-semibold">Uploaded Images</h3>
              <div className="flex gap-2">
                {!autoUpload && (
                  <Button
                    onClick={() => uploadImages(images.filter(img => img.status === 'uploading'))}
                    disabled={uploading || images.every(img => img.status !== 'uploading')}
                  >
                    <Upload className="h-4 w-4 mr-2" />
                    Upload All
                  </Button>
                )}
                <Button
                  variant="outline"
                  onClick={() => {
                    images.forEach(img => removeImage(img.id))
                  }}
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Clear All
                </Button>
              </div>
            </div>

            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {images.map((image) => (
                <div key={image.id} className="relative group">
                  <div className="aspect-square rounded-lg overflow-hidden bg-muted">
                    <img
                      src={image.thumbnailUrl || image.url}
                      alt={image.name}
                      className="w-full h-full object-cover"
                    />

                    {/* Overlay */}
                    <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
                      <div className="flex gap-2">
                        <Button
                          variant="secondary"
                          size="sm"
                          onClick={() => previewImage(image)}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                        {enableCrop && (
                          <Button
                            variant="secondary"
                            size="sm"
                          >
                            <Crop className="h-4 w-4" />
                          </Button>
                        )}
                        <Button
                          variant="destructive"
                          size="sm"
                          onClick={() => removeImage(image.id)}
                        >
                          <X className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>

                    {/* Status Indicator */}
                    <div className="absolute top-2 right-2">
                      {image.status === 'uploading' && (
                        <Badge variant="secondary" className="text-xs">
                          {image.uploadProgress}%
                        </Badge>
                      )}
                      {image.status === 'completed' && (
                        <CheckCircle className="h-5 w-5 text-green-500" />
                      )}
                      {image.status === 'failed' && (
                        <AlertCircle className="h-5 w-5 text-red-500" />
                      )}
                    </div>

                    {/* Progress Bar */}
                    {image.status === 'uploading' && (
                      <div className="absolute bottom-0 left-0 right-0">
                        <Progress value={image.uploadProgress} className="h-1" />
                      </div>
                    )}
                  </div>

                  {/* Image Info */}
                  <div className="mt-2 space-y-1">
                    <p className="text-sm font-medium truncate">{image.name}</p>
                    <div className="flex items-center justify-between text-xs text-muted-foreground">
                      <span>{formatFileSize(image.size)}</span>
                      {image.dimensions && (
                        <span>{image.dimensions.width}×{image.dimensions.height}</span>
                      )}
                    </div>
                    {image.error && (
                      <p className="text-xs text-red-500">{image.error}</p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Image Preview Modal */}
        {selectedImage && (
          <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/80">
            <div className="relative max-w-4xl max-h-screen p-4">
              <img
                src={selectedImage.url}
                alt={selectedImage.name}
                className="max-w-full max-h-full object-contain"
              />
              <Button
                variant="secondary"
                size="sm"
                className="absolute top-4 right-4"
                onClick={() => setSelectedImage(null)}
              >
                <X className="h-4 w-4" />
              </Button>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  )
}

