  8	// Cache Management
  9	class MemoryCache {
 10	  private cache = new Map<string, { data: any; timestamp: number; ttl: number }>();
 11	  private maxSize = 1000;
 12	
 13	  set(key: string, data: any, ttl: number = 300000): void { // 5 minutes default TTL
 14	    // Clean up if cache is too large
 15	    if (this.cache.size >= this.maxSize) {
 16	      const oldestKey = this.cache.keys().next().value;
 17	      if (oldestKey !== undefined) {
 18	        this.cache.delete(oldestKey);
 19	      }
 20	    }
 21	
 22	    this.cache.set(key, {
 23	      data,
 24	      timestamp: Date.now(),
 25	      ttl
 26	    });
 27	  }
 28	
 29	  get(key: string): any | null {
 30	    const item = this.cache.get(key);
 31	    if (!item) return null;
 32	
 33	    const now = Date.now();
 34	    if (now - item.timestamp > item.ttl) {
 35	      this.cache.delete(key);
 36	      return null;
 37	    }
 38	
 39	    return item.data;
 40	  }
 41	
