  updateWorkflow: (id: string, updates: Partial<FundingWorkflow>) => void;
  setVaultSelectedAccount: (accountId: string) => void;

  // UI Actions
  setTheme: (theme: 'light' | 'dark' | 'auto') => void;
  toggleSidebar: () => void;
