    },
    {
      id: 'portfolio',
      label: 'Portfolio',
      icon: <PieChart className="h-4 w-4" />,
      component: <PortfolioTab />
    },
    {
      id: 'agents',
      label: 'Expert Agents',
      icon: <Bot className="h-4 w-4" />,
      component: <ExpertAgentsPanel />
    },
    {
      id: 'farms',
      label: 'Trading Farms',
      icon: <Target className="h-4 w-4" />,
      component: <FarmsTab />
    },
    {
      id: 'analytics',
      label: 'Analytics',
      icon: <Activity className="h-4 w-4" />,
      component: <AnalyticsTab />
