    @apply bg-secondary;
  }

  ::-webkit-scrollbar-thumb {
    @apply bg-muted-foreground rounded-md;
  }

  ::-webkit-scrollbar-thumb:hover {
    @apply bg-foreground;
  }

  /* Focus styles */
  *:focus-visible {
    @apply ring-2 ring-ring ring-offset-2 ring-offset-background outline-none;
  }

  /* Selection styles */
