    @apply bg-status-error;
  }

  /* Chart container styles */
  .chart-container {
    @apply relative w-full bg-card border border-border rounded-lg overflow-hidden;
  }

  .chart-toolbar {
    @apply flex items-center justify-between p-3 bg-muted/30 border-b border-border;
  }

  /* Navigation styles */
  .nav-link {
    @apply flex items-center px-3 py-2 text-sm font-medium rounded-md transition-colors;
  }

  .nav-link-active {
    @apply bg-primary text-primary-foreground;
  }

  .nav-link-inactive {
    @apply text-muted-foreground hover:text-foreground hover:bg-accent;
  }

  /* Data table styles */
  .data-table {
    @apply w-full border-collapse border border-border rounded-lg overflow-hidden;
  }

  .data-table th {
    @apply bg-muted/50 px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider border-b border-border;
  }

  .data-table td {
    @apply px-4 py-3 text-sm border-b border-border;
  }

  .data-table tr:hover {
    @apply bg-muted/30;
  }

  /* Animation helpers */
  .animate-pulse-slow {
    animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  .animate-slide-in {
    animation: slideIn 0.3s ease-out;
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slideIn {
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
}

/* Custom utilities */
@layer utilities {
  .text-gradient {
    @apply bg-gradient-to-r from-primary to-primary/70 bg-clip-text text-transparent;
  }

  .glass-effect {
    @apply bg-background/80 backdrop-blur-sm border border-border/50;
  }

  .trading-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 1rem;
  }

  .dashboard-grid {
    display: grid;
    grid-template-columns: 250px 1fr;
    grid-template-rows: auto 1fr;
    min-height: 100vh;
  }

  .sidebar-grid {
    grid-column: 1;
    grid-row: 1 / -1;
  }

  .main-content {
    grid-column: 2;
    grid-row: 1 / -1;
  }

  @media (max-width: 768px) {
    .dashboard-grid {
      grid-template-columns: 1fr;
      grid-template-rows: auto auto 1fr;
    }

    .sidebar-grid {
      grid-column: 1;
      grid-row: 2;
    }

    .main-content {
      grid-column: 1;
      grid-row: 3;
    }
  }
}

