/**
 * Modern Trading Dashboard V4
 * Adapted for Vite + React from Next.js
 * Emerald/Violet/Amber color scheme with minimal mock data
 */

import React, { useEffect, useState } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { Progress } from '@/components/ui/progress'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import {
  Activity, TrendingUp, TrendingDown, DollarSign, Bot, Shield, Zap,
  Target, BarChart3, RefreshCw, Bell, Users, Calendar, Wallet, PieChart,
  Plus, Menu, X, Star, Clock, Settings, ArrowUpRight, ArrowDownRight
} from 'lucide-react'
import { LineChart, Line, AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts'

interface DashboardMetrics {
  totalValue: number
  dailyPnL: number
  totalPnL: number
  activePositions: number
  activeAgents: number
  activeFarms: number
  winRate: number
  avgReturn: number
  riskScore: number
}

interface ChartDataPoint {
  time: string
  value: number
  pnl: number
}

interface DashboardTab {
  id: string
  label: string
  icon: React.ReactNode
  component: React.ReactNode
}

// Mock chart data for portfolio performance
const generateChartData = (): ChartDataPoint[] => {
  const data: ChartDataPoint[] = []
  const now = new Date()

  for (let i = 29; i >= 0; i--) {
    const date = new Date(now.getTime() - i * 24 * 60 * 60 * 1000)
    const baseValue = 125000
    const variation = Math.sin(i * 0.1) * 5000 + Math.random() * 2000

    data.push({
      time: date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
      value: baseValue + variation,
      pnl: variation * 0.1
    })
  }

  return data
}

export function ModernDashboardV4() {
  const [activeTab, setActiveTab] = useState('overview')
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false)
  const [isLoading, setIsLoading] = useState(true)
  const [chartData] = useState<ChartDataPoint[]>(generateChartData())

  // Dashboard metrics state with v4 minimal mock data
  const [metrics, setMetrics] = useState<DashboardMetrics>({
    totalValue: 125847.32,
    dailyPnL: 2847.32,
    totalPnL: 25847.32,
    activePositions: 8,
    activeAgents: 5, // Our 5 expert agents
    activeFarms: 3,
    winRate: 78.4,
    avgReturn: 15.2,
    riskScore: 23.5
  })

  // Simulate loading completion
  useEffect(() => {
    const timer = setTimeout(() => setIsLoading(false), 1000)
    return () => clearTimeout(timer)
  }, [])

  // Tab configuration with v4 design
  const tabs: DashboardTab[] = [
    {
      id: 'overview',
      label: 'Overview',
      icon: <BarChart3 className="h-4 w-4" />,
      component: <OverviewTab metrics={metrics} chartData={chartData} />
    },
    {
      id: 'agents',
      label: 'Expert Agents',
      icon: <Bot className="h-4 w-4" />,
      component: <AgentsTab />
    },
    {
      id: 'farms',
      label: 'Trading Farms',
      icon: <Target className="h-4 w-4" />,
      component: <FarmsTab />
    },
    {
      id: 'goals',
      label: 'Goals',
      icon: <Star className="h-4 w-4" />,
      component: <GoalsTab />
    },
    {
      id: 'defi',
      label: 'DeFi Lending',
      icon: <DollarSign className="h-4 w-4" />,
      component: <DeFiTab />
    },
    {
      id: 'analytics',
      label: 'Analytics',
      icon: <Activity className="h-4 w-4" />,
      component: <AnalyticsTab />
    }
  ]

  if (isLoading) {
    return <LoadingScreen />
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-50 via-violet-50 to-amber-50 dark:from-gray-900 dark:via-gray-800 dark:to-gray-900">
      {/* Mobile Menu Overlay */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 bg-black/20 backdrop-blur-sm lg:hidden"
            onClick={() => setIsMobileMenuOpen(false)}
          />
        )}
      </AnimatePresence>

      <div className="flex h-screen">
        {/* Mobile Sidebar */}
        <AnimatePresence>
          {isMobileMenuOpen && (
            <motion.aside
              initial={{ x: '-100%' }}
              animate={{ x: 0 }}
              exit={{ x: '-100%' }}
              transition={{ type: 'spring', damping: 25, stiffness: 200 }}
              className="fixed inset-y-0 left-0 z-50 w-64 bg-white/95 backdrop-blur-md shadow-xl lg:hidden"
            >
              <MobileSidebar onClose={() => setIsMobileMenuOpen(false)} />
            </motion.aside>
          )}
        </AnimatePresence>

        {/* Desktop Sidebar */}
        <aside className="hidden lg:flex lg:flex-col lg:w-64 lg:bg-white/80 lg:backdrop-blur-md lg:border-r lg:border-emerald-200/50">
          <DesktopSidebar />
        </aside>

        {/* Main Content */}
        <main className="flex-1 flex flex-col overflow-hidden">
          {/* Header */}
          <header className="bg-white/80 backdrop-blur-md border-b border-emerald-200/50 p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <Button
                  variant="ghost"
                  size="sm"
                  className="lg:hidden"
                  onClick={() => setIsMobileMenuOpen(true)}
                >
                  <Menu className="h-5 w-5" />
                </Button>
                <div>
                  <h1 className="text-2xl font-bold bg-gradient-to-r from-emerald-600 via-violet-600 to-amber-600 bg-clip-text text-transparent">
                    Cival Dashboard v4
                  </h1>
                  <p className="text-sm text-gray-600">Advanced AI Trading Platform</p>
                </div>
              </div>

              <div className="flex items-center gap-3">
                <Badge variant="secondary" className="bg-emerald-100 text-emerald-800 border-emerald-200">
                  Live
                </Badge>
                <Button variant="outline" size="sm" className="hidden sm:flex">
                  <Bell className="h-4 w-4 mr-2" />
                  Alerts
                </Button>
                <Button variant="outline" size="sm">
                  <Settings className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </header>

          {/* Dashboard Content */}
          <div className="flex-1 overflow-auto p-6">
            <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
              <TabsList className="grid w-full grid-cols-3 md:grid-cols-6 bg-white/50 backdrop-blur-sm">
                {tabs.map((tab) => (
                  <TabsTrigger
                    key={tab.id}
                    value={tab.id}
                    className="flex items-center gap-2 data-[state=active]:bg-emerald-100 data-[state=active]:text-emerald-800"
                  >
                    {tab.icon}
                    <span className="hidden sm:inline">{tab.label}</span>
                  </TabsTrigger>
                ))}
              </TabsList>

              {tabs.map((tab) => (
                <TabsContent key={tab.id} value={tab.id}>
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3 }}
                  >
                    {tab.component}
                  </motion.div>
                </TabsContent>
              ))}
            </Tabs>
          </div>
        </main>
      </div>
    </div>
  )
}

// Overview Tab Component
function OverviewTab({ metrics, chartData }: { metrics: DashboardMetrics; chartData: ChartDataPoint[] }) {
  return (
    <div className="space-y-6">
      {/* Metrics Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <MetricCard
          title="Total Portfolio"
          value={`$${metrics.totalValue.toLocaleString('en-US', { minimumFractionDigits: 2 })}`}
          change={`+${metrics.dailyPnL.toFixed(2)}`}
          changePercent="+2.34%"
          isPositive={metrics.dailyPnL > 0}
          icon={<DollarSign className="h-5 w-5" />}
          gradient="from-emerald-500 to-emerald-600"
        />

        <MetricCard
          title="Active Agents"
          value={metrics.activeAgents.toString()}
          change="Expert Agents"
          isPositive={true}
          icon={<Bot className="h-5 w-5" />}
          gradient="from-violet-500 to-violet-600"
        />

        <MetricCard
          title="Win Rate"
          value={`${metrics.winRate}%`}
          change="Last 30 days"
          isPositive={metrics.winRate > 70}
          icon={<Target className="h-5 w-5" />}
          gradient="from-amber-500 to-amber-600"
        />

        <MetricCard
          title="Risk Score"
          value={`${metrics.riskScore}%`}
          change="Low Risk"
          isPositive={metrics.riskScore < 30}
          icon={<Shield className="h-5 w-5" />}
          gradient="from-rose-500 to-rose-600"
        />
      </div>

      {/* Performance Chart */}
      <Card className="bg-white/80 backdrop-blur-sm border-emerald-200/50">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <BarChart3 className="h-5 w-5 text-emerald-600" />
            Portfolio Performance
          </CardTitle>
          <CardDescription>30-day portfolio value and P&L trend</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <AreaChart data={chartData}>
                <defs>
                  <linearGradient id="portfolioGradient" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="#10b981" stopOpacity={0.3}/>
                    <stop offset="95%" stopColor="#10b981" stopOpacity={0}/>
                  </linearGradient>
                </defs>
                <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                <XAxis dataKey="time" stroke="#6b7280" />
                <YAxis stroke="#6b7280" />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: '1px solid #d1fae5',
                    borderRadius: '8px',
                    backdropFilter: 'blur(4px)'
                  }}
                />
                <Area
                  type="monotone"
                  dataKey="value"
                  stroke="#10b981"
                  strokeWidth={2}
                  fill="url(#portfolioGradient)"
                />
              </AreaChart>
            </ResponsiveContainer>
          </div>
        </CardContent>
      </Card>

      {/* Quick Actions */}
      <Card className="bg-white/80 backdrop-blur-sm border-emerald-200/50">
        <CardHeader>
          <CardTitle>Quick Actions</CardTitle>
          <CardDescription>Frequently used trading operations</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid gap-3 md:grid-cols-3">
            <Button className="bg-gradient-to-r from-emerald-500 to-emerald-600 hover:from-emerald-600 hover:to-emerald-700">
              <Plus className="h-4 w-4 mr-2" />
              New Strategy
            </Button>
            <Button variant="outline" className="border-violet-200 text-violet-700 hover:bg-violet-50">
              <Bot className="h-4 w-4 mr-2" />
              Deploy Agent
            </Button>
            <Button variant="outline" className="border-amber-200 text-amber-700 hover:bg-amber-50">
              <Target className="h-4 w-4 mr-2" />
              Create Farm
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

// Metric Card Component with v4 gradients
function MetricCard({ 
  title, 
  value, 
  change, 
  changePercent, 
  isPositive, 
  icon, 
  gradient 
}: {
  title: string
  value: string
  change: string
  changePercent?: string
  isPositive: boolean
  icon: React.ReactNode
  gradient: string
}) {
  return (
    <Card className="bg-white/80 backdrop-blur-sm border-emerald-200/50 hover:shadow-lg transition-all duration-300">
      <CardContent className="p-6">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-gray-600">{title}</p>
            <p className="text-2xl font-bold text-gray-900">{value}</p>
            <div className="flex items-center gap-1 mt-1">
              {isPositive ? (
                <ArrowUpRight className="h-3 w-3 text-emerald-600" />
              ) : (
                <ArrowDownRight className="h-3 w-3 text-rose-600" />
              )}
              <span className={`text-xs font-medium ${isPositive ? 'text-emerald-600' : 'text-rose-600'}`}>
                {change}
              </span>
              {changePercent && (
                <span className={`text-xs ${isPositive ? 'text-emerald-600' : 'text-rose-600'}`}>
                  {changePercent}
                </span>
              )}
            </div>
          </div>
          <div className={`p-3 rounded-full bg-gradient-to-r ${gradient} text-white`}>
            {icon}
          </div>
        </div>
      </CardContent>
    </Card>
  )
}

// Agents Tab Component
function AgentsTab() {
  return (
    <Card className="bg-white/80 backdrop-blur-sm border-emerald-200/50">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Bot className="h-5 w-5 text-violet-600" />
          Expert Agents
        </CardTitle>
        <CardDescription>5 specialized AI trading agents with autonomous learning</CardDescription>
      </CardHeader>
      <CardContent>
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {[
            { name: 'Darvas Box Expert', status: 'Active', performance: '+12.4%', color: 'emerald' },
            { name: 'Elliott Wave Expert', status: 'Active', performance: '+8.7%', color: 'violet' },
