            {filteredFarms.map((farm) => (
              <FarmCard
                key={farm.id}
                farm={farm}
                onClick={() => setSelectedFarm(farm)}
              />
            ))}
          </div>
        )}
      </div>

      {/* Create Farm Dialog */}
      {showCreateDialog && (
        <CreateFarmDialog
          isOpen={showCreateDialog}
