          onSubmit={handleCreateFarm}
          availableAgents={agents}
        />
      )}

      {/* Farm Details Panel */}
      {selectedFarm && (
        <FarmDetailsPanel
          farm={selectedFarm}
          isOpen={!!selectedFarm}
          onClose={() => setSelectedFarm(null)}
        />
      )}
    </div>
  );
};

export default FarmsManager;

