          }));

          get().broadcastWalletUpdate();

        } catch (error) {
          set({ error: 'Failed to withdraw funds', loading: false });
          console.error('Withdraw error:', error);
        }
      },

      transferToAgent: async (agentId: string, amount: number, token: string) => {
        try {
