            id: `tx-${Date.now()}`,
            type: 'transfer',
            amount,
            token,
            status: 'confirmed',
            timestamp: new Date().toISOString(),
            from: get().address,
            agentId
          };

          set(state => ({
            transactions: [transaction, ...state.transactions]
          }));

          // Update agent balance through AG-UI
          const agUIProtocol = AgUIProtocol.getInstance();
          await agUIProtocol.sendEvent('agent_funding', {
            agentId,
