      <div className="grid gap-6 lg:grid-cols-2">
                {/* Recent Transactions */}        <Card>          <CardHeader>            <CardTitle>Recent Transactions</CardTitle>            <CardDescription>              Cross-vault transfers and operations            </CardDescription>          </CardHeader>          <CardContent className="p-0">            <Table>              <TableHeader>                <TableRow>                  <TableHead>Type</TableHead>                  <TableHead>From/To</TableHead>                  <TableHead>Amount</TableHead>                  <TableHead>Status</TableHead>                  <TableHead>Time</TableHead>                </TableRow>              </TableHeader>              <TableBody>                {recentTransactions.map((tx) => (                  <TableRow key={tx.id}>                    <TableCell>                      <div className="flex items-center space-x-2">                        {getTransactionIcon(tx.type)}                        <span className="font-medium capitalize">{tx.type}</span>                      </div>                    </TableCell>                    <TableCell>                      <div className="space-y-1">                        <div className="text-sm font-medium">{tx.from} ‚Üí {tx.to}</div>                        <div className="text-xs text-muted-foreground">{tx.purpose}</div>                      </div>                    </TableCell>                    <TableCell>                      <div className="font-semibold">{formatPrice(tx.amount)}</div>                    </TableCell>                    <TableCell>                      <Badge variant={                        tx.status === 'completed' ? 'success' :                         tx.status === 'pending' ? 'outline' :                         tx.status === 'failed' ? 'destructive' : 'secondary'                      }>                        {tx.status}                      </Badge>                    </TableCell>                    <TableCell>                      <div className="text-sm">                        {new Date(tx.timestamp).toLocaleTimeString()}                      </div>                      <div className="text-xs text-muted-foreground">                        {tx.reference}                      </div>                    </TableCell>                  </TableRow>                ))}              </TableBody>            </Table>          </CardContent>        </Card>

        {/* DeFi Protocol Integration */}
        <Card>
