  1	import { create } from 'zustand';
  2	import { persist } from 'zustand/middleware';
  3	import { backendClient } from '@/lib/api/backend-client';
  4	import { AgUIProtocol } from '@/lib/ag-ui-protocol-v2';
  5	
  6	interface WalletBalance {
  7	  token: string;
  8	  symbol: string;
  9	  amount: number;
 10	  value: number; // USD value
 11	  price: number; // USD price per token
 12	}
 13	
 14	interface Transaction {
 15	  id: string;
 16	  type: 'deposit' | 'withdrawal' | 'trade' | 'transfer';
 17	  amount: number;
 18	  token: string;
 19	  hash?: string;
 20	  status: 'pending' | 'confirmed' | 'failed';
 21	  timestamp: string;
 22	  from?: string;
 23	  to?: string;
 24	  agentId?: string;
 25	  farmId?: string;
 26	}
 27	
 28	interface WalletStore {
 29	  // State
 30	  totalBalance: number;
 31	  balances: WalletBalance[];
 32	  transactions: Transaction[];
 33	  isConnected: boolean;
 34	  address?: string;
 35	  loading: boolean;
 36	  error: string | null;
 37	
 38	  // Actions
 39	  connectWallet: () => Promise<void>;
 40	  disconnectWallet: () => void;
 41	  loadBalance: () => Promise<void>;
 42	  deposit: (amount: number, token: string) => Promise<void>;
 43	  withdraw: (amount: number, token: string, address: string) => Promise<void>;
 44	  transferToAgent: (agentId: string, amount: number, token: string) => Promise<void>;
 45	  transferToFarm: (farmId: string, amount: number, token: string) => Promise<void>;
 46	  getTransactionHistory: () => Promise<void>;
 47	  refreshPrices: () => Promise<void>;
 48	  
 49	  // AG-UI Integration
 50	  initializeAGUI: () => void;
 51	  broadcastWalletUpdate: () => void;
 52	}
 53	
 54	export const useWalletStore = create<WalletStore>()(
 55	  persist(
 56	    (set, get) => ({
 57	      totalBalance: 0,
 58	      balances: [],
 59	      transactions: [],
 60	      isConnected: false,
 61	      address: undefined,
 62	      loading: false,
 63	      error: null,
 64	
 65	      connectWallet: async () => {
 66	        set({ loading: true, error: null });
 67	        try {
 68	          // Simulate wallet connection - replace with actual wallet integration
 69	          const mockAddress = '0x742d35cc4bf78d7362c6b07a67dc15c3c1b47a99';
 70	          
 71	          // Initialize with some mock balances
 72	          const mockBalances: WalletBalance[] = [
 73	            {
 74	              token: 'USDC',
 75	              symbol: 'USDC',
 76	              amount: 50.0, // Start with $50 as requested
 77	              value: 50.0,
 78	              price: 1.0
 79	            },
 80	            {
 81	              token: 'ETH',
 82	              symbol: 'ETH',
 83	              amount: 0.02,
 84	              value: 64.8,
 85	              price: 3240.0
 86	            },
 87	            {
 88	              token: 'BTC',
 89	              symbol: 'BTC',
 90	              amount: 0.001,
 91	              value: 97.5,
 92	              price: 97500.0
 93	            }
 94	          ];
 95	
 96	          const totalBalance = mockBalances.reduce((sum, balance) => sum + balance.value, 0);
 97	
 98	          set({
 99	            isConnected: true,
100	            address: mockAddress,
101	            balances: mockBalances,
102	            totalBalance,
103	            loading: false
104	          });
105	
106	          // Initialize AG-UI Protocol
107	          get().initializeAGUI();
108	          get().broadcastWalletUpdate();
109	
110	        } catch (error) {
111	          set({ error: 'Failed to connect wallet', loading: false });
112	          console.error('Wallet connection error:', error);
113	        }
114	      },
115	
116	      disconnectWallet: () => {
117	        set({
118	          isConnected: false,
119	          address: undefined,
120	          balances: [],
121	          totalBalance: 0,
122	          transactions: []
123	        });
124	      },
125	
126	      loadBalance: async () => {
127	        if (!get().isConnected) return;
128	        
129	        set({ loading: true, error: null });
130	        try {
131	          // In a real implementation, this would fetch from blockchain/API
132	          await get().refreshPrices();
133	          get().broadcastWalletUpdate();
134	        } catch (error) {
135	          set({ error: 'Failed to load balance', loading: false });
136	          console.error('Load balance error:', error);
137	        }
138	      },
139	
140	      deposit: async (amount: number, token: string) => {
141	        set({ loading: true, error: null });
142	        try {
143	          const { balances } = get();
144	          const existingBalance = balances.find(b => b.token === token);
145	          
146	          if (existingBalance) {
147	            const updatedBalances = balances.map(balance =>
148	              balance.token === token
149	                ? { 
150	                    ...balance, 
151	                    amount: balance.amount + amount,
152	                    value: (balance.amount + amount) * balance.price
153	                  }
154	                : balance
155	            );
156	            
157	            const totalBalance = updatedBalances.reduce((sum, balance) => sum + balance.value, 0);
158	            set({ balances: updatedBalances, totalBalance });
159	          } else {
160	            // Add new token balance
161	            const price = token === 'USDC' ? 1.0 : token === 'ETH' ? 3240.0 : 97500.0;
162	            const newBalance: WalletBalance = {
163	              token,
164	              symbol: token,
165	              amount,
166	              value: amount * price,
167	              price
168	            };
169	            
170	            const updatedBalances = [...balances, newBalance];
171	            const totalBalance = updatedBalances.reduce((sum, balance) => sum + balance.value, 0);
172	            set({ balances: updatedBalances, totalBalance });
173	          }
174	
175	          // Record transaction
176	          const transaction: Transaction = {
177	            id: `tx-${Date.now()}`,
178	            type: 'deposit',
179	            amount,
180	            token,
181	            status: 'confirmed',
182	            timestamp: new Date().toISOString(),
183	            to: get().address
184	          };
185	
186	          set(state => ({
187	            transactions: [transaction, ...state.transactions],
188	            loading: false
189	          }));
190	
191	          get().broadcastWalletUpdate();
192	
193	        } catch (error) {
194	          set({ error: 'Failed to deposit funds', loading: false });
195	          console.error('Deposit error:', error);
196	        }
197	      },
198	
199	      withdraw: async (amount: number, token: string, address: string) => {
200	        set({ loading: true, error: null });
201	        try {
202	          const { balances } = get();
203	          const balance = balances.find(b => b.token === token);
204	          
205	          if (!balance || balance.amount < amount) {
206	            throw new Error('Insufficient balance');
207	          }
208	
209	          const updatedBalances = balances.map(b =>
210	            b.token === token
211	              ? { 
212	                  ...b, 
213	                  amount: b.amount - amount,
214	                  value: (b.amount - amount) * b.price
215	                }
216	              : b
217	          );
218	
219	          const totalBalance = updatedBalances.reduce((sum, balance) => sum + balance.value, 0);
220	          set({ balances: updatedBalances, totalBalance });
221	
222	          // Record transaction
223	          const transaction: Transaction = {
224	            id: `tx-${Date.now()}`,
225	            type: 'withdrawal',
226	            amount,
227	            token,
228	            status: 'pending',
229	            timestamp: new Date().toISOString(),
230	            from: get().address,
231	            to: address
232	          };
233	
234	          set(state => ({
235	            transactions: [transaction, ...state.transactions],
236	            loading: false
237	          }));
238	
239	          get().broadcastWalletUpdate();
240	
241	        } catch (error) {
242	          set({ error: 'Failed to withdraw funds', loading: false });
243	          console.error('Withdraw error:', error);
244	        }
245	      },
246	
247	      transferToAgent: async (agentId: string, amount: number, token: string) => {
248	        try {
249	          const { balances } = get();
250	          const balance = balances.find(b => b.token === token);
251	          
252	          if (!balance || balance.amount < amount) {
253	            throw new Error('Insufficient balance');
254	          }
255	
256	          // Update wallet balance
257	          const updatedBalances = balances.map(b =>
258	            b.token === token
259	              ? { 
260	                  ...b, 
261	                  amount: b.amount - amount,
262	                  value: (b.amount - amount) * b.price
263	                }
264	              : b
265	          );
266	
267	          const totalBalance = updatedBalances.reduce((sum, balance) => sum + balance.value, 0);
268	          set({ balances: updatedBalances, totalBalance });
269	
270	          // Record transaction
271	          const transaction: Transaction = {
272	            id: `tx-${Date.now()}`,
273	            type: 'transfer',
274	            amount,
275	            token,
276	            status: 'confirmed',
277	            timestamp: new Date().toISOString(),
278	            from: get().address,
279	            agentId
280	          };
281	
282	          set(state => ({
283	            transactions: [transaction, ...state.transactions]
284	          }));
285	
286	          // Update agent balance through AG-UI
287	          const agUIProtocol = AgUIProtocol.getInstance();
288	          await agUIProtocol.sendEvent('agent_funding', {
289	            agentId,
290	            amount,
291	            token,
292	            transactionId: transaction.id
293	          });
294	
295	          get().broadcastWalletUpdate();
296	
297	        } catch (error) {
298	          console.error('Transfer to agent error:', error);
299	          throw error;
300	        }
301	      },
302	
303	      transferToFarm: async (farmId: string, amount: number, token: string) => {
304	        try {
305	          const { balances } = get();
306	          const balance = balances.find(b => b.token === token);
307	          
308	          if (!balance || balance.amount < amount) {
309	            throw new Error('Insufficient balance');
310	          }
311	
312	          // Update wallet balance
313	          const updatedBalances = balances.map(b =>
314	            b.token === token
315	              ? { 
316	                  ...b, 
317	                  amount: b.amount - amount,
318	                  value: (b.amount - amount) * b.price
319	                }
320	              : b
321	          );
322	
323	          const totalBalance = updatedBalances.reduce((sum, balance) => sum + balance.value, 0);
324	          set({ balances: updatedBalances, totalBalance });
325	
326	          // Record transaction
327	          const transaction: Transaction = {
328	            id: `tx-${Date.now()}`,
329	            type: 'transfer',
330	            amount,
331	            token,
332	            status: 'confirmed',
333	            timestamp: new Date().toISOString(),
334	            from: get().address,
335	            farmId
336	          };
337	
338	          set(state => ({
339	            transactions: [transaction, ...state.transactions]
340	          }));
341	
342	          // Update farm balance through AG-UI
343	          const agUIProtocol = AgUIProtocol.getInstance();
344	          await agUIProtocol.sendEvent('farm_funding', {
345	            farmId,
346	            amount,
347	            token,
348	            transactionId: transaction.id
349	          });
350	
351	          get().broadcastWalletUpdate();
