import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import { backendClient } from '@/lib/api/backend-client';

interface Agent {
  id: string;
  name: string;
  type: 'trading' | 'arbitrage' | 'market-maker' | 'yield-farmer' | 'scout';
  status: 'active' | 'inactive' | 'error' | 'paused';
  balance: number;
  pnl24h: number;
  pnlPercent: number;
  trades24h: number;
  winRate: number;
  lastActive: string;
  strategy: string;
  settings: {
    maxRisk: number;
    targetReturn: number;
    stopLoss: number;
    takeProfit: number;
  };
  performance: {
    totalTrades: number;
    totalVolume: number;
    avgReturn: number;
    maxDrawdown: number;
    sharpeRatio: number;
  };
  farmId?: string;
  walletAddress?: string;
  created: string;
}

interface AgentStore {
  agents: Agent[];
  selectedAgent: Agent | null;
  loading: boolean;
  error: string | null;

  // Actions
  fetchAgents: () => Promise<void>;
  createAgent: (agentData: Partial<Agent>) => Promise<void>;
  updateAgent: (id: string, updates: Partial<Agent>) => Promise<void>;
  deleteAgent: (id: string) => Promise<void>;
  startAgent: (id: string) => Promise<void>;
  stopAgent: (id: string) => Promise<void>;
  pauseAgent: (id: string) => Promise<void>;
  setSelectedAgent: (agent: Agent | null) => void;
  refreshAgentData: (id: string) => Promise<void>;
  fundAgent: (id: string, amount: number) => Promise<void>;
  withdrawFromAgent: (id: string, amount: number) => Promise<void>;
}

export const useAgentStore = create<AgentStore>()(
  persist(
    (set, get) => ({
      agents: [],
