import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import { backendClient } from '@/lib/api/backend-client';

interface Agent {
  id: string;
  name: string;
  type: 'trading' | 'arbitrage' | 'market-maker' | 'yield-farmer' | 'scout';
  status: 'active' | 'inactive' | 'error' | 'paused';
  balance: number;
  pnl24h: number;
  pnlPercent: number;
  trades24h: number;
  winRate: number;
  lastActive: string;
  strategy: string;
  settings: {
    maxRisk: number;
    targetReturn: number;
    stopLoss: number;
    takeProfit: number;
